     9→    static func parseSymbols(from text: String) -> [String] {
    10→        // 공백, 쉼표, 탭, 새줄 문자로 구분된 심볼들을 추출
    11→        let separators = CharacterSet(charactersIn: " ,\t\n\r")
    12→        let components = text.components(separatedBy: separators)
    13→        
    14→        return components
    15→            .map { $0.trimmingCharacters(in: .whitespacesAndNewlines) }
    16→            .filter { !$0.isEmpty }
    17→            .map { $0.uppercased() }
    18→            .removingDuplicates()
    19→    }
