import Foundation

/// 다양한 형태의 심볼 입력을 파싱하는 유틸리티
struct TickerSymbolParser {

    /// 입력된 텍스트에서 심볼들을 추출합니다
    /// - Parameter text: 사용자가 입력한 텍스트
    /// - Returns: 정리된 심볼들의 배열
    static func parseSymbols(from text: String) -> [String] {
        // 공백, 쉼표, 탭, 새줄 문자로 구분된 심볼들을 추출
        let separators = CharacterSet(charactersIn: " ,\t\n\r")
        let components = text.components(separatedBy: separators)

        return components
            .map { $0.trimmingCharacters(in: .whitespacesAndNewlines) }
            .filter { !$0.isEmpty }
            .map { $0.uppercased() }
            .removingDuplicates()
    }

    /// 단일 심볼이 유효한 형식인지 검증합니다
    /// - Parameter symbol: 검증할 심볼
    /// - Returns: 유효하면 true
    static func isValidSymbol(_ symbol: String) -> Bool {
        // 기본적인 심볼 형식 검증
        let symbolPattern = "^[A-Z]{1,10}(-[A-Z])?$"
        let regex = try? NSRegularExpression(pattern: symbolPattern)
        let range = NSRange(location: 0, length: symbol.count)
        return regex?.firstMatch(in: symbol, options: [], range: range) != nil
    }

    /// 입력된 텍스트가 다중 심볼인지 판단합니다
    /// - Parameter text: 검사할 텍스트
    /// - Returns: 다중 심볼이면 true
    static func isMultipleSymbols(_ text: String) -> Bool {
        let symbols = parseSymbols(from: text)
        return symbols.count > 1
    }
}

extension Array where Element: Equatable {
    /// 중복을 제거한 배열을 반환합니다
    func removingDuplicates() -> [Element] {
        var result: [Element] = []
        for element in self {
            if !result.contains(element) {
                result.append(element)
            }
        }
        return result
    }
}
