/// 주식 데이터 관리 에러
public enum StockDataManagerError: Error, LocalizedError {
    case invalidTicker(String)
    case notFound(String)
    case modelContextRequired
    case containerCreationFailed(Error)

    public var errorDescription: String? {
        switch self {
        case .invalidTicker(let symbol):
            return "Invalid symbol symbol: \(symbol)"
        case .notFound(let symbol):
            return "Stock data not found for symbol: \(symbol)"
        case .modelContextRequired:
            return "ModelContext is required for this operation"
        case .containerCreationFailed(let error):
            return "Failed to create ModelContainer: \(error.localizedDescription)"
        }
    }
}

