import Foundation
import SwiftUI
import SwiftData
import StockData
import StockModel

/// 차트 뷰의 데이터 관리 및 비즈니스 로직을 담당하는 ViewModel
@MainActor
class ChartViewModel: ObservableObject {

    // MARK: - Published Properties

    /// 포트폴리오 전체 요약 정보
    @Published var portfolioSummary: PortfolioSummary = .empty()

    /// 섹터별 성과 정보
    @Published var sectorPerformances: [String: SectorPerformanceData] = [:]

    /// 상위 종목 목록 (최대 8개)
    @Published var topStocks: [ChartStockData] = []

    /// 로딩 상태
    @Published var isLoading: Bool = false

    /// 에러 상태
    @Published var hasError: Bool = false

    /// 에러 메시지
    @Published var errorMessage: String?

    // MARK: - Private Properties

    private let stockDataManager: StockDataManager
    private let configuration: ChartConfiguration
    private var refreshTimer: Timer?

    // MARK: - Initialization

    init(stockDataManager: StockDataManager, configuration: ChartConfiguration = ChartConfiguration()) {
        self.stockDataManager = stockDataManager
        self.configuration = configuration

        setupAutoRefresh()
    }

    deinit {
        // Timer 정리는 setupAutoRefresh의 weak self 참조로 자동 처리됨
    }

    // MARK: - Public Methods

    /// 초기 데이터 로딩
    func loadInitialData() async {
        await performDataLoad()
    }

    /// 데이터 새로고침
    func refreshData() async {
        await performDataLoad()
    }

    // MARK: - Private Methods

    /// 실제 데이터 로딩 수행
    private func performDataLoad() async {
        isLoading = true
        hasError = false
        errorMessage = nil

        // StockData에서 모든 주식 데이터 가져오기
        let allProfiles = stockDataManager.getAllProfiles()
        let stockData = allProfiles.map { ChartStockData(from: $0) }

        // 포트폴리오 요약 계산
        portfolioSummary = calculatePortfolioSummary(from: stockData)

        // 섹터별 성과 계산
        sectorPerformances = SectorPerformanceCalculator.calculateSectorPerformance(from: stockData)

        // 상위 종목 선정
        topStocks = Array(selectTopStocks(from: stockData).prefix(configuration.maxItemsInGrid))

        isLoading = false
    }

    /// 포트폴리오 전체 요약 정보 계산
    private func calculatePortfolioSummary(from stocks: [ChartStockData]) -> PortfolioSummary {
        guard !stocks.isEmpty else {
            return .empty()
        }

        var totalValue = 0.0
        var totalCost = 0.0
        var totalDayChange = 0.0

        for stock in stocks {
            guard let quote = stock.quote, !stock.positions.isEmpty else { continue }

            // 현재 가치 계산
            let stockPositions = convertToStockPositions(stock.positions)
            let currentValue = PortfolioCalculator.calculateCurrentValue(
                from: stockPositions,
                currentPrice: quote.currentPrice
            )
            totalValue += currentValue

            // 총 비용 계산
            let cost = PortfolioCalculator.calculateTotalCost(from: stockPositions)
            totalCost += cost

            // 당일 변동 계산
            let dayChange = calculateDayChange(for: stock)
            totalDayChange += dayChange
        }

        // 총 손익 계산
        let totalGainLoss = totalValue - totalCost
        let totalGainLossPercent = totalCost > 0 ? (totalGainLoss / totalCost) * 100 : 0.0

        // 당일 변동률 계산
        let previousValue = totalValue - totalDayChange
        let dayChangePercent = previousValue > 0 ? (totalDayChange / previousValue) * 100 : 0.0

        return PortfolioSummary(
            totalValue: totalValue,
            totalGainLoss: totalGainLoss,
            totalGainLossPercent: totalGainLossPercent,
            dayChange: totalDayChange,
            dayChangePercent: dayChangePercent,
            lastUpdated: Date()
        )
    }

    /// 개별 주식의 당일 변동 계산
    private func calculateDayChange(for stock: ChartStockData) -> Double {
        guard let quote = stock.quote, !stock.positions.isEmpty else { return 0.0 }

        let totalShares = stock.positions.reduce(0) { $0 + $1.shares }
        return quote.changeAmount * Double(totalShares)
    }

    /// 상위 종목 선정 (포지션 가치 기준)
    private func selectTopStocks(from stocks: [ChartStockData]) -> [ChartStockData] {
        return stocks
            .filter { !$0.positions.isEmpty && $0.quote != nil }
            .sorted { lhs, rhs in
                let lhsValue = calculateStockValue(lhs)
                let rhsValue = calculateStockValue(rhs)
                return lhsValue > rhsValue
            }
    }

    /// 개별 주식의 포지션 가치 계산
    private func calculateStockValue(_ stock: ChartStockData) -> Double {
        guard let quote = stock.quote else { return 0.0 }
        let stockPositions = convertToStockPositions(stock.positions)
        return PortfolioCalculator.calculateCurrentValue(
            from: stockPositions,
            currentPrice: quote.currentPrice
        )
    }

    /// ChartPositionData를 StockPosition으로 변환
    private func convertToStockPositions(_ positionData: [ChartPositionData]) -> [StockPosition] {
        return positionData.map { data in
            let position = StockPosition(
                symbol: data.symbol,
                pricePerShare: data.averagePrice,
                quantity: Double(data.shares),
                dateAdded: data.purchaseDate
            )
            return position
        }
    }

    /// 자동 새로고침 설정
    private func setupAutoRefresh() {
        // 테스트 환경에서는 자동 새로고침 비활성화
        guard configuration.refreshInterval > 0 else { return }

        refreshTimer = Timer.scheduledTimer(withTimeInterval: configuration.refreshInterval, repeats: true) { [weak self] _ in
            guard let self = self else { return }
            Task { @MainActor in
                await self.refreshData()
            }
        }
    }
}

// MARK: - ChartStockData 확장

/// StockModel.StockProfileData를 Stocks.ChartStockData로 변환하기 위한 확장
extension ChartStockData {
    init(from stockProfile: StockModel.StockProfileData) {
        self.symbol = stockProfile.symbol
        self.companyName = stockProfile.name
        self.sector = stockProfile.sector
        self.industry = stockProfile.industry

        // Quote 변환
        if let profileQuote = stockProfile.quote {
            self.quote = ChartQuoteData(
                symbol: profileQuote.symbol,
                currentPrice: profileQuote.currentPrice,
                changeAmount: profileQuote.change,
                changePercent: profileQuote.changePercent,
                volume: profileQuote.volume,
                avgVolume: 0, // StockQuoteData에는 avgVolume 필드가 없음
                marketCap: 0, // StockQuoteData에는 marketCap 필드가 없음
                peRatio: 0, // StockQuoteData에는 peRatio 필드가 없음
                lastUpdated: profileQuote.lastUpdated
            )
        } else {
            self.quote = nil
        }

        // Positions 변환
        self.positions = stockProfile.positions.map { position in
            ChartPositionData(
                symbol: position.symbol,
                shares: Int(position.quantity),  // quantity -> shares 변환
                averagePrice: position.pricePerShare,  // pricePerShare -> averagePrice 변환
                purchaseDate: position.dateAdded  // dateAdded -> purchaseDate 변환
            )
        }

        // Financials 변환 (필요한 경우)
        if let profileFinancials = stockProfile.financials {
            self.financials = ChartFinancialData(
                symbol: profileFinancials.symbol,
                marketCap: profileFinancials.marketCap,
                enterpriseValue: 0.0, // StockDetailData에 없는 필드
                peRatio: profileFinancials.trailingPE, // trailingPE -> peRatio 변환
                pegRatio: 0.0, // StockDetailData에 없는 필드
                bookValue: 0.0, // StockDetailData에 없는 필드
                priceToBook: 0.0, // StockDetailData에 없는 필드
                lastUpdated: Date() // StockDetailData에 없는 필드, 현재 시간 사용
            )
        } else {
            self.financials = nil
        }
    }
}

