/// 섹터별 성과 정보를 계산하고 저장하는 데이터 모델
struct SectorPerformanceData {
    /// 섹터명
    let sectorName: String

    /// 섹터 내 종목 수
    let stockCount: Int

    /// 섹터 평균 변동률 (%)
    let changePercent: Double

    /// 상승 종목 수
    let upCount: Int

    /// 하락 종목 수
    let downCount: Int

    /// 보합 종목 수
    let flatCount: Int

    /// 포트폴리오 내 섹터 비중 (%)
    let portfolioWeight: Double

    /// 섹터별 총 손익 금액
    let totalGainLoss: Double

    /// 섹터별 총 손익률 (%)
    let totalGainLossPercent: Double

    /// 마지막 업데이트 시간
    let lastUpdated: Date

    /// 빈 데이터 생성자
    static func empty(sectorName: String) -> SectorPerformanceData {
        SectorPerformanceData(
            sectorName: sectorName,
            stockCount: 0,
            changePercent: 0.0,
            upCount: 0,
            downCount: 0,
            flatCount: 0,
            portfolioWeight: 0.0,
            totalGainLoss: 0.0,
            totalGainLossPercent: 0.0,
            lastUpdated: Date()
        )
    }
}

/// 섹터별 성과 계산 유틸리티
class SectorPerformanceCalculator {

    /// 기존 StockModel.StockProfileData용 섹터별 성과 계산 (기존 UI 호환성 유지)
    /// - Parameter stocks: 계산할 주식 데이터 배열
    /// - Returns: 섹터별 성과 정보 딕셔너리
    static func calculateSectorPerformance(from stocks: [StockModel.StockProfileData]) -> [String: SectorPerformanceData] {
        // 기존 로직을 StockModel.StockProfileData에 맞게 구현
        let groupedStocks = Dictionary(grouping: stocks) { $0.sector.isEmpty ? "기타" : $0.sector }
        var sectorPerformances: [String: SectorPerformanceData] = [:]

        // 전체 포트폴리오 가치 계산 (비중 계산용)
        let totalPortfolioValue = calculateTotalPortfolioValueFromStockModel(from: stocks)

        for (sectorName, sectorStocks) in groupedStocks {
            let performance = calculateSectorPerformanceFromStockModel(
                sectorName: sectorName,
                stocks: sectorStocks,
                totalPortfolioValue: totalPortfolioValue
            )
            sectorPerformances[sectorName] = performance
        }

        return sectorPerformances
    }

    /// ChartView용 섹터별 성과 계산
    /// - Parameter stocks: 계산할 주식 데이터 배열
    /// - Returns: 섹터별 성과 정보 딕셔너리
    static func calculateSectorPerformance(from stocks: [ChartStockData]) -> [String: SectorPerformanceData] {
        let groupedStocks = Dictionary(grouping: stocks) { $0.sector.isEmpty ? "기타" : $0.sector }
        var sectorPerformances: [String: SectorPerformanceData] = [:]

        // 전체 포트폴리오 가치 계산 (비중 계산용)
        let totalPortfolioValue = calculateTotalPortfolioValue(from: stocks)

        for (sectorName, sectorStocks) in groupedStocks {
            let performance = calculateSectorPerformance(
                sectorName: sectorName,
                stocks: sectorStocks,
                totalPortfolioValue: totalPortfolioValue
            )
            sectorPerformances[sectorName] = performance
        }

        return sectorPerformances
    }

    /// 개별 섹터의 성과를 계산합니다
    private static func calculateSectorPerformance(
        sectorName: String,
        stocks: [ChartStockData],
        totalPortfolioValue: Double
    ) -> SectorPerformanceData {

        let stockCount = stocks.count
        var upCount = 0
        var downCount = 0
        var flatCount = 0

        var totalChangeWeighted = 0.0
        var totalMarketCap = 0.0
        var sectorPortfolioValue = 0.0
        var totalGainLoss = 0.0

        for stock in stocks {
            // 시세 정보 확인
            guard let quote = stock.quote else { continue }

            let changePercent = quote.changePercent
            let marketCap = stock.financials?.marketCap ?? 0.0

            // 상승/하락/보합 분류
            if changePercent > 0.01 {
                upCount += 1
            } else if changePercent < -0.01 {
                downCount += 1
            } else {
                flatCount += 1
            }

            // 시가총액 가중 평균을 위한 계산
            if marketCap > 0 {
                totalChangeWeighted += changePercent * marketCap
                totalMarketCap += marketCap
            }

            // 포지션 기반 수익률 계산
            let positionValue = calculatePositionValue(for: stock)
            sectorPortfolioValue += positionValue

            if !stock.positions.isEmpty {
                let stockPositions = convertToStockPositions(stock.positions)
                let gainLoss = PortfolioCalculator.calculateGainLoss(
                    from: stockPositions,
                    currentPrice: quote.currentPrice
                )
                totalGainLoss += gainLoss
            }
        }

        // 섹터 평균 변동률 계산 (시가총액 가중)
        let sectorChangePercent = totalMarketCap > 0 ? totalChangeWeighted / totalMarketCap : 0.0

        // 포트폴리오 비중 계산
        let portfolioWeight = totalPortfolioValue > 0 ? (sectorPortfolioValue / totalPortfolioValue) * 100 : 0.0

        // 섹터 전체 손익률 계산
        let totalCost = stocks.reduce(0.0) { sum, stock in
            let stockPositions = convertToStockPositions(stock.positions)
            return sum + PortfolioCalculator.calculateTotalCost(from: stockPositions)
        }
        let totalGainLossPercent = totalCost > 0 ? (totalGainLoss / totalCost) * 100 : 0.0

        return SectorPerformanceData(
            sectorName: sectorName,
            stockCount: stockCount,
            changePercent: sectorChangePercent,
            upCount: upCount,
            downCount: downCount,
            flatCount: flatCount,
            portfolioWeight: portfolioWeight,
            totalGainLoss: totalGainLoss,
            totalGainLossPercent: totalGainLossPercent,
            lastUpdated: Date()
        )
    }

    /// 전체 포트폴리오 가치 계산
    private static func calculateTotalPortfolioValue(from stocks: [ChartStockData]) -> Double {
        return stocks.reduce(0.0) { total, stock in
            total + calculatePositionValue(for: stock)
        }
    }

    /// 개별 주식의 포지션 가치 계산
    private static func calculatePositionValue(for stock: ChartStockData) -> Double {
        guard let quote = stock.quote, !stock.positions.isEmpty else { return 0.0 }

        let stockPositions = convertToStockPositions(stock.positions)
        return PortfolioCalculator.calculateCurrentValue(
            from: stockPositions,
            currentPrice: quote.currentPrice
        )
    }

    /// ChartPositionData를 StockPosition으로 변환
    private static func convertToStockPositions(_ positionData: [ChartPositionData]) -> [StockPosition] {
        return positionData.map { data in
            let position = StockPosition(
                symbol: data.symbol,
                pricePerShare: data.averagePrice,
                quantity: Double(data.shares),
                dateAdded: data.purchaseDate
            )
            return position
        }
    }

    // MARK: - StockModel.StockProfileData용 메서드들

    /// StockModel.StockProfileData용 개별 섹터 성과 계산
    private static func calculateSectorPerformanceFromStockModel(
        sectorName: String,
        stocks: [StockModel.StockProfileData],
        totalPortfolioValue: Double
    ) -> SectorPerformanceData {

        let stockCount = stocks.count
        var upCount = 0
        var downCount = 0
        var flatCount = 0

        var totalChangeWeighted = 0.0
        var totalMarketCap = 0.0
        var sectorPortfolioValue = 0.0
        var totalGainLoss = 0.0

        for stock in stocks {
            // 시세 정보 확인
            guard let quote = stock.quote else { continue }

            let changePercent = quote.changePercent
            let marketCap = stock.financials?.marketCap ?? 0.0

            // 상승/하락/보합 분류
            if changePercent > 0.01 {
                upCount += 1
            } else if changePercent < -0.01 {
                downCount += 1
            } else {
                flatCount += 1
            }

            // 시가총액 가중 평균을 위한 계산
            if marketCap > 0 {
                totalChangeWeighted += changePercent * marketCap
                totalMarketCap += marketCap
            }

            // 포지션 기반 수익률 계산
            let positionValue = calculatePositionValueFromStockModel(for: stock)
            sectorPortfolioValue += positionValue

            if !stock.positions.isEmpty {
                let gainLoss = PortfolioCalculator.calculateGainLoss(
                    from: stock.positions,
                    currentPrice: quote.currentPrice
                )
                totalGainLoss += gainLoss
            }
        }

        // 섹터 평균 변동률 계산 (시가총액 가중)
        let sectorChangePercent = totalMarketCap > 0 ? totalChangeWeighted / totalMarketCap : 0.0

        // 포트폴리오 비중 계산
        let portfolioWeight = totalPortfolioValue > 0 ? (sectorPortfolioValue / totalPortfolioValue) * 100 : 0.0

        // 섹터 전체 손익률 계산
        let totalCost = stocks.reduce(0.0) { sum, stock in
            sum + PortfolioCalculator.calculateTotalCost(from: stock.positions)
        }
        let totalGainLossPercent = totalCost > 0 ? (totalGainLoss / totalCost) * 100 : 0.0

        return SectorPerformanceData(
            sectorName: sectorName,
            stockCount: stockCount,
            changePercent: sectorChangePercent,
            upCount: upCount,
            downCount: downCount,
            flatCount: flatCount,
            portfolioWeight: portfolioWeight,
            totalGainLoss: totalGainLoss,
            totalGainLossPercent: totalGainLossPercent,
            lastUpdated: Date()
        )
    }

    /// StockModel.StockProfileData용 전체 포트폴리오 가치 계산
    private static func calculateTotalPortfolioValueFromStockModel(from stocks: [StockModel.StockProfileData]) -> Double {
        return stocks.reduce(0.0) { total, stock in
            total + calculatePositionValueFromStockModel(for: stock)
        }
    }

    /// StockModel.StockProfileData용 개별 주식의 포지션 가치 계산
    private static func calculatePositionValueFromStockModel(for stock: StockModel.StockProfileData) -> Double {
        guard let quote = stock.quote, !stock.positions.isEmpty else { return 0.0 }

        return PortfolioCalculator.calculateCurrentValue(
            from: stock.positions,
            currentPrice: quote.currentPrice
        )
    }
}
