import SwiftUI
import StockModel

/// FlowLayout의 효과를 시연하는 뷰
struct FlowLayoutDemoView: View {
    let quote: StockQuoteData
    let financials: StockDetailData?

    var body: some View {
        ScrollView {
            VStack(alignment: .leading, spacing: 20) {

                // 기존 LazyVGrid 방식
                VStack(alignment: .leading, spacing: 12) {
                    Text("기존 LazyVGrid 방식")
                        .font(.headline)
                        .foregroundStyle(.primary)

                    LazyVGrid(columns: Array(repeating: GridItem(.adaptive(minimum: 120)), count: 4), spacing: 12) {
                        priceMetrics
                    }
                }

                Divider()

                // 새로운 FlowLayout 방식
                VStack(alignment: .leading, spacing: 12) {
                    Text("새로운 FlowLayout 방식")
                        .font(.headline)
                        .foregroundStyle(.primary)

                    Group {
                        priceMetrics
                    }
                    .flowLayout(spacing: 12, alignment: .leading)
                }

                if let financials = financials {
                    Divider()

                    // 재무 지표도 FlowLayout으로
                    VStack(alignment: .leading, spacing: 12) {
                        Text("재무 지표 FlowLayout")
                            .font(.headline)
                            .foregroundStyle(.primary)

                        Group {
                            financialMetrics(financials)
                        }
                        .flowLayout(spacing: 12, alignment: .leading)
                    }
                }

                // 다양한 크기의 태그들로 FlowLayout 효과 시연
                Divider()

                VStack(alignment: .leading, spacing: 12) {
                    Text("다양한 크기 태그 FlowLayout")
                        .font(.headline)
                        .foregroundStyle(.primary)

                    Group {
                        tagViews
                    }
                    .flowLayout(spacing: 8, alignment: .leading)
                }
            }
            .padding()
        }
        .navigationTitle("FlowLayout Demo")
        .navigationBarTitleDisplayMode(.inline)
    }

    @ViewBuilder
    private var priceMetrics: some View {
        if quote.fiftyTwoWeekHigh > 0 {
            PriceMetricView(
                title: "52W High",
                value: String(format: "$%.2f", quote.fiftyTwoWeekHigh),
                icon: "arrow.up.circle"
            )
        }

        if quote.fiftyTwoWeekLow > 0 {
            PriceMetricView(
                title: "52W Low",
                value: String(format: "$%.2f", quote.fiftyTwoWeekLow),
                icon: "arrow.down.circle"
            )
        }

        if quote.volume > 0 {
            PriceMetricView(
                title: "Volume",
                value: formatVolume(quote.volume),
                icon: "chart.bar.fill"
            )
        }

        if quote.fiftyDayAverage > 0 {
            PriceMetricView(
                title: "50-Day Avg",
                value: String(format: "$%.2f", quote.fiftyDayAverage),
                icon: "chart.line.uptrend.xyaxis"
            )
        }

        if quote.twoHundredDayAverage > 0 {
            PriceMetricView(
                title: "200-Day Avg",
                value: String(format: "$%.2f", quote.twoHundredDayAverage),
                icon: "chart.xyaxis.line"
            )
        }

        if quote.previousClose > 0 {
            PriceMetricView(
                title: "Previous Close",
                value: String(format: "$%.2f", quote.previousClose),
                icon: "clock.fill"
            )
        }
    }

    @ViewBuilder
    private func financialMetrics(_ financials: StockDetailData) -> some View {
        if financials.trailingPE > 0 {
            FinancialDetailMetricView(
                title: "P/E Ratio",
                value: String(format: "%.1f", financials.trailingPE),
                icon: "chart.pie"
            )
        }

        if financials.beta > 0 {
            FinancialDetailMetricView(
                title: "Beta",
                value: String(format: "%.2f", financials.beta),
                icon: "waveform.path.ecg"
            )
        }

        if financials.dividendYield > 0 {
            FinancialDetailMetricView(
                title: "Dividend Yield",
                value: String(format: "%.2f%%", financials.dividendYield * 100),
                icon: "percent"
            )
        }

        if financials.marketCap > 0 {
            FinancialDetailMetricView(
                title: "Market Cap",
                value: formatMarketCap(financials.marketCap),
                icon: "building.2"
            )
        }
    }

    @ViewBuilder
    private var tagViews: some View {
        Group {
            TagView(text: "Technology", color: .blue)
            TagView(text: "Large Cap", color: .green)
            TagView(text: "High Growth", color: .purple)
            TagView(text: "Dividend", color: .orange)
            TagView(text: "S&P 500", color: .red)
            TagView(text: "NASDAQ", color: .mint)
            TagView(text: "Consumer Electronics", color: .indigo)
            TagView(text: "Innovation", color: .pink)
            TagView(text: "ESG", color: .teal)
            TagView(text: "AI", color: .cyan)
        }
    }

    private func formatVolume(_ volume: Int64) -> String {
        let millions = Double(volume) / 1_000_000
        if millions >= 1000 {
            return String(format: "%.1fB", millions / 1000)
        } else if millions >= 1 {
            return String(format: "%.1fM", millions)
        } else {
            let thousands = Double(volume) / 1000
            return String(format: "%.0fK", thousands)
        }
    }

    private func formatMarketCap(_ marketCap: Double) -> String {
        let trillions = marketCap / 1_000_000_000_000
        if trillions >= 1 {
            return String(format: "%.2fT", trillions)
        } else {
            let billions = marketCap / 1_000_000_000
            return String(format: "%.0fB", billions)
        }
    }
}

/// 태그를 표시하는 간단한 뷰
struct TagView: View {
    let text: String
    let color: Color

    var body: some View {
        Text(text)
            .font(.caption)
            .fontWeight(.medium)
            .padding(.horizontal, 12)
            .padding(.vertical, 6)
            .background(color.opacity(0.1))
            .foregroundStyle(color)
            .clipShape(.capsule)
            .overlay(
                Capsule()
                    .stroke(color.opacity(0.3), lineWidth: 1)
            )
    }
}

#Preview("FlowLayout Demo") {
    NavigationView {
        FlowLayoutDemoView(
            quote: StockQuoteData.appleSample(),
            financials: StockDetailData.appleSample()
        )
    }
}

