    func refreshAllData() async {
        // 이전 태스크 취소
        refreshTask?.cancel()

        refreshTask = Task {
            guard !Task.isCancelled else { return }

            isRefreshing = true
            lastError = nil

            defer {
                isRefreshing = false
            }

            do {
                // Task 취소 확인
                try Task.checkCancellation()

                // Summary 데이터 요청 (가격 데이터는 전역 서비스에서 자동 처리됨)
                let summaryResponse = try await stockAPI.getQuoteSummary(
                    symbol: symbol,
                    modules: [.summaryProfile, .summaryDetail]
                )

                // Task 취소 확인
                try Task.checkCancellation()

                // Summary 데이터 저장 (가격 데이터는 전역 서비스에서 관리됨)
                if let result = summaryResponse.quoteSummary.result?.first {
                    // 기존 Profile 가져오기 또는 생성
                    let profile: StockProfileData
                    if let existingProfile = dataManager.getProfile(for: symbol) {
                        profile = existingProfile
                    } else {
                        // Profile이 없으면 생성 (placeholder)
                        profile = try dataManager.createPlaceholderProfile(symbol: symbol, name: symbol)
                    }

                    // ProfileDetails 업데이트 (summaryProfile이 있는 경우)
                    if let summaryProfile = result.summaryProfile {
                        if let existingDetails = dataManager.getDetails(for: symbol) {
                            existingDetails.update(from: summaryProfile)
                        } else {
                            let details = StockProfileDetails(from: summaryProfile, symbol: symbol)

