import Foundation
import StockModel

/// 일주일간의 가격 변동 차트 데이터
struct WeeklyChartData: Identifiable, Hashable {
    let id = UUID()
    let date: Date
    let price: Double

    init(timestamp: Int, price: Double) {
        self.date = Date(timeIntervalSince1970: TimeInterval(timestamp))
        self.price = price
    }
}

/// 일주일 차트 데이터 처리 유틸리티
struct WeeklyChartDataProcessor {

    /// Yahoo Finance Chart API 응답을 WeeklyChartData 배열로 변환
    /// - Parameter chartResponse: Yahoo Finance API 응답
    /// - Returns: 일주일간의 차트 데이터 배열
    static func processChartData(_ chartResponse: ChartAPIResponse) -> [WeeklyChartData] {
        guard let result = chartResponse.chart.result?.first,
              let timestamps = result.timestamp,
              let quotes = result.indicators?.quote?.first,
              let closePrices = quotes.close else {
            return []
        }

        var chartData: [WeeklyChartData] = []

        for (index, timestamp) in timestamps.enumerated() {
            if index < closePrices.count,
               let price = closePrices[index] {
                chartData.append(WeeklyChartData(timestamp: timestamp, price: price))
            }
        }

        return chartData.sorted { $0.date < $1.date }
    }

    /// 차트 데이터에서 가격 변동률 계산
    /// - Parameter data: 차트 데이터 배열
    /// - Returns: 첫 번째와 마지막 가격 간의 변동률 (백분율)
    static func calculatePriceChange(_ data: [WeeklyChartData]) -> Double {
        guard let first = data.first,
              let last = data.last,
              first.price > 0 else {
            return 0.0
        }

        return ((last.price - first.price) / first.price) * 100
    }

    /// 차트 데이터에서 최고가와 최저가 계산
    /// - Parameter data: 차트 데이터 배열
    /// - Returns: (최저가, 최고가) 튜플
    static func calculatePriceRange(_ data: [WeeklyChartData]) -> (min: Double, max: Double) {
        guard !data.isEmpty else {
            return (0, 0)
        }

        let prices = data.map { $0.price }
        return (prices.min() ?? 0, prices.max() ?? 0)
    }
}

