import Foundation
import StockAPI
import StockModel
import SwiftUI

/// 일주일간의 차트 데이터를 관리하는 서비스
@MainActor
@Observable
final class WeeklyChartService {

    /// 차트 데이터 캐시 (심볼별)
    private var chartDataCache: [String: [WeeklyChartData]] = [:]

    /// 로딩 상태 (심볼별)
    private var loadingStates: [String: Bool] = [:]

    /// 에러 상태 (심볼별)
    private var errorStates: [String: Error?] = [:]

    /// Yahoo Finance API 클라이언트
    private let stockAPI: YahooFinanceClient

    /// 초기화
    /// - Parameter stockAPI: Yahoo Finance API 클라이언트
    init(stockAPI: YahooFinanceClient) {
        self.stockAPI = stockAPI
    }

    /// 특정 심볼의 차트 데이터 가져오기
    /// - Parameter symbol: 주식 심볼
    /// - Returns: 차트 데이터 배열
    func getChartData(for symbol: String) -> [WeeklyChartData] {
        return chartDataCache[symbol] ?? []
    }

    /// 특정 심볼의 로딩 상태 확인
    /// - Parameter symbol: 주식 심볼
    /// - Returns: 로딩 중이면 true
    func isLoading(for symbol: String) -> Bool {
        return loadingStates[symbol] ?? false
    }

    /// 특정 심볼의 에러 상태 확인
    /// - Parameter symbol: 주식 심볼
    /// - Returns: 에러가 있으면 해당 에러, 없으면 nil
    func getError(for symbol: String) -> Error? {
        return errorStates[symbol] ?? nil
    }

    /// 일주일간의 차트 데이터 로드
    /// - Parameter symbol: 주식 심볼
    func loadWeeklyChart(for symbol: String) async {
        // 이미 로딩 중인 경우 중복 요청 방지
        guard !isLoading(for: symbol) else { return }

        loadingStates[symbol] = true
        errorStates[symbol] = nil

        do {
            // 7일간의 1시간 간격 데이터 요청
            let response = try await stockAPI.getHistory(
                symbol: symbol,
                period: "7d",
                interval: "1h",
                includePrePost: false
            )

            // 차트 데이터 처리
            let chartData = WeeklyChartDataProcessor.processChartData(response)

            // 캐시에 저장
            chartDataCache[symbol] = chartData

        } catch {
            print("Failed to load weekly chart for \(symbol): \(error)")
            errorStates[symbol] = error
            chartDataCache[symbol] = []
        }

        loadingStates[symbol] = false
    }

    /// 차트 데이터 캐시 클리어
    /// - Parameter symbol: 클리어할 심볼 (nil이면 전체 클리어)
    func clearCache(for symbol: String? = nil) {
        if let symbol = symbol {
            chartDataCache.removeValue(forKey: symbol)
            loadingStates.removeValue(forKey: symbol)
            errorStates.removeValue(forKey: symbol)
        } else {
            chartDataCache.removeAll()
            loadingStates.removeAll()
            errorStates.removeAll()
        }
    }

    /// 차트 데이터 새로고침
    /// - Parameter symbol: 주식 심볼
    func refreshChart(for symbol: String) async {
        clearCache(for: symbol)
        await loadWeeklyChart(for: symbol)
    }
}

/// WeeklyChartService를 위한 Environment 확장
extension EnvironmentValues {
    @Entry var weeklyChartService: WeeklyChartService? = nil
}

