/// Stock 앱의 환경 오브젝트
/// StockDataManager와 관련 서비스들을 관리합니다
@Observable
public final class StockEnvironment {

    /// 주식 데이터 관리자
    public let dataManager: StockDataManager

    /// 초기화
    /// - Parameter dataManager: 사용할 StockDataManager
    public init(dataManager: StockDataManager) {
        self.dataManager = dataManager
    }

    /// 프로덕션용 StockEnvironment 생성
    /// - Parameter persistent: 영구 저장 여부 (기본값: true)
    /// - Returns: 생성된 StockEnvironment
    /// - Throws: StockDataManagerError.containerCreationFailed
    @MainActor
    public static func production(persistent: Bool = true) throws -> StockEnvironment {
        let dataManager = try StockDataManager.production(persistent: persistent)
        return StockEnvironment(dataManager: dataManager)
    }

    /// 프리뷰용 StockEnvironment 생성
    /// - Returns: 생성된 StockEnvironment
    @MainActor
    public static func preview() -> StockEnvironment {
        let dataManager = StockDataManager.preview()
        return StockEnvironment(dataManager: dataManager)
    }

    /// ModelContainer에 접근
    public var container: ModelContainer? {
        return dataManager.container
    }

    public var isEditMode: Bool = false

    public var isChartMode: Bool = false

    public var search: String = ""
}

