import XCTest
import StockModel
@testable import Stocks

/// WeeklyChartData 모델과 처리 유틸리티 테스트
final class WeeklyChartDataTests: XCTestCase {

    // MARK: - WeeklyChartData 테스트

    func testWeeklyChartDataInitialization() {
        // Given
        let timestamp = Int(Date().timeIntervalSince1970)
        let price = 150.25

        // When
        let chartData = WeeklyChartData(timestamp: timestamp, price: price)

        // Then
        XCTAssertEqual(chartData.price, price)
        XCTAssertEqual(chartData.date.timeIntervalSince1970, TimeInterval(timestamp), accuracy: 1.0)
    }

    func testWeeklyChartDataEquality() {
        // Given
        let timestamp = Int(Date().timeIntervalSince1970)
        let price = 150.25

        // When
        let chartData1 = WeeklyChartData(timestamp: timestamp, price: price)
        let chartData2 = WeeklyChartData(timestamp: timestamp, price: price)

        // Then
        // ID가 다르므로 다른 객체로 인식되어야 함
        XCTAssertNotEqual(chartData1.id, chartData2.id)
    }

    // MARK: - WeeklyChartDataProcessor 테스트

    func testProcessChartDataWithValidData() {
        // Given
        let timestamps = [1640995200, 1641081600, 1641168000] // 예시 타임스탬프
        let closePrices: [Double?] = [150.0, 152.5, 148.8]

        let chartQuoteData = ChartQuoteData(close: closePrices)
        let chartIndicators = ChartIndicators(quote: [chartQuoteData])
        let chartData = ChartData(
            meta: nil,
            timestamp: timestamps,
            indicators: chartIndicators
        )
        let chartResponse = ChartAPIResponse(
            chart: ChartResponseContainer(result: [chartData])
        )

        // When
        let result = WeeklyChartDataProcessor.processChartData(chartResponse)

        // Then
        XCTAssertEqual(result.count, 3)
        XCTAssertEqual(result[0].price, 150.0)
        XCTAssertEqual(result[1].price, 152.5)
        XCTAssertEqual(result[2].price, 148.8)

        // 날짜 순서 확인
        for i in 1..<result.count {
