        // Then
        XCTAssertEqual(chartData.price, price)
        XCTAssertEqual(chartData.date.timeIntervalSince1970, TimeInterval(timestamp), accuracy: 1.0)
    }

    func testWeeklyChartDataEquality() {
        // Given
        let timestamp = Int(Date().timeIntervalSince1970)
        let price = 150.25

        // When
