import Charts
import SwiftUI
import StockModel

/// 일주일간의 가격 변동을 보여주는 소형 차트 뷰
struct WeeklyPriceChartView: View {
    let data: [WeeklyChartData]
    let symbol: String
    let isLoading: Bool

    var priceChangePercentage: Double {
        WeeklyChartDataProcessor.calculatePriceChange(data)
    }

    var isPositive: Bool {
        priceChangePercentage >= 0
    }

    var chartColor: Color {
        isPositive ? .green : .red
    }

    /// 차트의 Y축 도메인 계산 (동적 스케일링)
    var yAxisDomain: ClosedRange<Double> {
        guard !data.isEmpty else { return 0...100 }

        let prices = data.map { $0.price }
        let minPrice = prices.min() ?? 0
        let maxPrice = prices.max() ?? 0

        // 가격 범위가 너무 작으면 패딩을 추가
        let range = maxPrice - minPrice
        let padding = max(range * 0.1, 0.01) // 10% 패딩 또는 최소 0.01

        let adjustedMin = max(0, minPrice - padding)
        let adjustedMax = maxPrice + padding

        return adjustedMin...adjustedMax
    }

    /// 차트 높이를 동적으로 조정
    var chartHeight: CGFloat {
        guard !data.isEmpty else { return 60 }

        let priceRange = yAxisDomain.upperBound - yAxisDomain.lowerBound
        let changePercent = abs(priceChangePercentage)

        // 변동률이 클수록 높이를 증가 (최소 60, 최대 100)
        if changePercent > 5.0 {
            return 100
        } else if changePercent > 2.0 {
            return 80
        } else {
            return 60
        }
    }

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            // 헤더
            HStack {
                Label("일주일 변동", systemImage: "chart.line.uptrend.xyaxis")
                    .font(.caption.weight(.medium))
                    .foregroundStyle(.secondary)

                Spacer()

                // 변동률 표시
                Text("\(priceChangePercentage >= 0 ? "+" : "")\(priceChangePercentage, specifier: "%.2f")%")
                    .font(.caption.weight(.semibold))
                    .foregroundStyle(chartColor)
            }

            // 차트
            Group {
                if isLoading {
                    // 로딩 상태
                    loadingView
                } else if data.isEmpty {
                    // 빈 상태
                    emptyStateView
                } else {
                    Chart(data) { item in
                        LineMark(
                            x: .value("Date", item.date),
                            y: .value("Price", item.price)
                        )
                        .foregroundStyle(chartColor)
                        .lineStyle(StrokeStyle(lineWidth: 2.5))
                        .symbol(.circle)
                        .symbolSize(30)

                        AreaMark(
                            x: .value("Date", item.date),
                            y: .value("Price", item.price)
                        )
                        .foregroundStyle(
                            LinearGradient(
                                colors: [chartColor.opacity(0.4), chartColor.opacity(0.05)],
                                startPoint: .top,
                                endPoint: .bottom
                            )
                        )
                    }
                    .frame(height: chartHeight)
                    .chartYScale(domain: yAxisDomain)
                    .chartXAxis(.hidden)
                    .chartYAxis(.hidden)
                    .chartLegend(.hidden)
                    .clipShape(RoundedRectangle(cornerRadius: 8))
                }
            }
            .animation(.easeInOut(duration: 0.3), value: data.count)

            // 기간 정보
            if !data.isEmpty {
                HStack {
                    if let firstDate = data.first?.date,
                       let lastDate = data.last?.date {
                        Text("\(firstDate, format: .dateTime.month(.abbreviated).day()) - \(lastDate, format: .dateTime.month(.abbreviated).day())")
                            .font(.caption2)
                            .foregroundStyle(.tertiary)
                    }

                    Spacer()

                    Text("\(data.count)개 데이터 포인트")
                        .font(.caption2)
                        .foregroundStyle(.tertiary)
                }
            }
        }
        .padding(.vertical, 4)
    }
}

