import Foundation
import SwiftUI
import SwiftData
import StockData
import StockModel

/// 차트 뷰의 데이터 관리 및 비즈니스 로직을 담당하는 ViewModel
@MainActor
class ChartViewModel: ObservableObject {

    // MARK: - Published Properties

    /// 포트폴리오 전체 요약 정보
    @Published var portfolioSummary: PortfolioSummary = .empty()

    /// 섹터별 성과 정보
    @Published var sectorPerformances: [String: SectorPerformanceData] = [:]

    /// 상위 종목 목록 (최대 8개)
    @Published var topStocks: [ChartStockData] = []

    /// 로딩 상태
    @Published var isLoading: Bool = false

    /// 에러 상태
    @Published var hasError: Bool = false

    /// 에러 메시지
    @Published var errorMessage: String?

    // MARK: - Private Properties

    private let stockDataManager: StockDataManager
    private let configuration: ChartConfiguration
    private var refreshTimer: Timer?

    // MARK: - Initialization

    init(stockDataManager: StockDataManager, configuration: ChartConfiguration = ChartConfiguration()) {
        self.stockDataManager = stockDataManager
        self.configuration = configuration

        setupAutoRefresh()
    }

    deinit {
        // Timer 정리는 setupAutoRefresh의 weak self 참조로 자동 처리됨
    }

    // MARK: - Public Methods

    /// 초기 데이터 로딩
    func loadInitialData() async {
        await performDataLoad()
    }

    /// 데이터 새로고침
    func refreshData() async {
        await performDataLoad()
    }

    // MARK: - Private Methods

    /// 실제 데이터 로딩 수행
    private func performDataLoad() async {
        isLoading = true
        hasError = false
        errorMessage = nil

        // StockData에서 모든 주식 데이터 가져오기
        let allProfiles = stockDataManager.getAllProfiles()
        let stockData = allProfiles.map { ChartStockData(from: $0) }

        // 포트폴리오 요약 계산
        portfolioSummary = calculatePortfolioSummary(from: stockData)

        // 섹터별 성과 계산
        sectorPerformances = SectorPerformanceCalculator.calculateSectorPerformance(from: stockData)

        // 상위 종목 선정
        topStocks = Array(selectTopStocks(from: stockData).prefix(configuration.maxItemsInGrid))

        isLoading = false
    }

    /// 포트폴리오 전체 요약 정보 계산
    private func calculatePortfolioSummary(from stocks: [ChartStockData]) -> PortfolioSummary {
        guard !stocks.isEmpty else {
            return .empty()
        }

        var totalValue = 0.0
        var totalCost = 0.0
        var totalDayChange = 0.0

        for stock in stocks {
            guard let quote = stock.quote, !stock.positions.isEmpty else { continue }

            // 현재 가치 계산
            let stockPositions = convertToStockPositions(stock.positions)
            let currentValue = PortfolioCalculator.calculateCurrentValue(
                from: stockPositions,
                currentPrice: quote.currentPrice
            )
            totalValue += currentValue

            // 총 비용 계산
            let cost = PortfolioCalculator.calculateTotalCost(from: stockPositions)
            totalCost += cost

            // 당일 변동 계산
            let dayChange = calculateDayChange(for: stock)
            totalDayChange += dayChange
        }

        // 총 손익 계산
        let totalGainLoss = totalValue - totalCost
        let totalGainLossPercent = totalCost > 0 ? (totalGainLoss / totalCost) * 100 : 0.0

        // 당일 변동률 계산
        let previousValue = totalValue - totalDayChange
        let dayChangePercent = previousValue > 0 ? (totalDayChange / previousValue) * 100 : 0.0

        return PortfolioSummary(
            totalValue: totalValue,
            totalGainLoss: totalGainLoss,
            totalGainLossPercent: totalGainLossPercent,
            dayChange: totalDayChange,
            dayChangePercent: dayChangePercent,
            lastUpdated: Date()
        )
    }

    /// 개별 주식의 당일 변동 계산
    private func calculateDayChange(for stock: ChartStockData) -> Double {
        guard let quote = stock.quote, !stock.positions.isEmpty else { return 0.0 }

        let totalShares = stock.positions.reduce(0) { $0 + $1.shares }
        return quote.changeAmount * Double(totalShares)
    }

    /// 상위 종목 선정 (포지션 가치 기준)
    private func selectTopStocks(from stocks: [ChartStockData]) -> [ChartStockData] {
        return stocks
            .filter { !$0.positions.isEmpty && $0.quote != nil }
            .sorted { lhs, rhs in
                let lhsValue = calculateStockValue(lhs)
                let rhsValue = calculateStockValue(rhs)
                return lhsValue > rhsValue
            }
    }

    /// 개별 주식의 포지션 가치 계산
    private func calculateStockValue(_ stock: ChartStockData) -> Double {
        guard let quote = stock.quote else { return 0.0 }
        let stockPositions = convertToStockPositions(stock.positions)
        return PortfolioCalculator.calculateCurrentValue(
            from: stockPositions,
            currentPrice: quote.currentPrice
        )
    }

    /// ChartPositionData를 StockPosition으로 변환
    private func convertToStockPositions(_ positionData: [ChartPositionData]) -> [StockPosition] {
        return positionData.map { data in
            let position = StockPosition(
                symbol: data.symbol,
                pricePerShare: data.averagePrice,
                quantity: Double(data.shares),
                dateAdded: data.purchaseDate
            )
            return position
        }
    }

    /// 자동 새로고침 설정
    private func setupAutoRefresh() {
        // 테스트 환경에서는 자동 새로고침 비활성화
        guard configuration.refreshInterval > 0 else { return }

        refreshTimer = Timer.scheduledTimer(withTimeInterval: configuration.refreshInterval, repeats: true) { [weak self] _ in
            guard let self = self else { return }
            Task { @MainActor in
                await self.refreshData()
            }
        }
    }
}

// MARK: - ChartStockData 확장

/// StockModel.StockProfileData를 Stocks.ChartStockData로 변환하기 위한 확장
extension ChartStockData {
    init(from stockProfile: StockModel.StockProfileData) {
        self.symbol = stockProfile.symbol
        self.companyName = stockProfile.name
        self.sector = stockProfile.sector
        self.industry = stockProfile.industry

        // Quote 변환
        if let profileQuote = stockProfile.quote {
            self.quote = ChartQuoteData(
                symbol: profileQuote.symbol,
                currentPrice: profileQuote.currentPrice,
                changeAmount: profileQuote.change,
                changePercent: profileQuote.changePercent,
                volume: profileQuote.volume,
                avgVolume: 0, // StockQuoteData에는 avgVolume 필드가 없음
                marketCap: 0, // StockQuoteData에는 marketCap 필드가 없음
                peRatio: 0, // StockQuoteData에는 peRatio 필드가 없음
                lastUpdated: profileQuote.lastUpdated
            )
        } else {
            self.quote = nil
        }

        // Positions 변환
        self.positions = stockProfile.positions.map { position in
            ChartPositionData(
                symbol: position.symbol,
                shares: Int(position.quantity),  // quantity -> shares 변환
                averagePrice: position.pricePerShare,  // pricePerShare -> averagePrice 변환
                purchaseDate: position.dateAdded  // dateAdded -> purchaseDate 변환
            )
        }

        // Financials 변환 (필요한 경우)
        if let profileFinancials = stockProfile.financials {
            self.financials = ChartFinancialData(
                symbol: profileFinancials.symbol,
                marketCap: profileFinancials.marketCap,
                enterpriseValue: 0.0, // StockDetailData에 없는 필드
                peRatio: profileFinancials.trailingPE, // trailingPE -> peRatio 변환
                pegRatio: 0.0, // StockDetailData에 없는 필드
                bookValue: 0.0, // StockDetailData에 없는 필드
                priceToBook: 0.0, // StockDetailData에 없는 필드
                lastUpdated: Date() // StockDetailData에 없는 필드, 현재 시간 사용
            )
        } else {
            self.financials = nil
        }
    }
}

import Foundation
import SwiftData
import StockModel
import StockData

/// 섹터별 성과 정보를 계산하고 저장하는 데이터 모델
struct SectorPerformanceData {
    /// 섹터명
    let sectorName: String

    /// 섹터 내 종목 수
    let stockCount: Int

    /// 섹터 평균 변동률 (%)
    let changePercent: Double

    /// 상승 종목 수
    let upCount: Int

    /// 하락 종목 수
    let downCount: Int

    /// 보합 종목 수
    let flatCount: Int

    /// 포트폴리오 내 섹터 비중 (%)
    let portfolioWeight: Double

    /// 섹터별 총 손익 금액
    let totalGainLoss: Double

    /// 섹터별 총 손익률 (%)
    let totalGainLossPercent: Double

    /// 마지막 업데이트 시간
    let lastUpdated: Date

    /// 빈 데이터 생성자
    static func empty(sectorName: String) -> SectorPerformanceData {
        SectorPerformanceData(
            sectorName: sectorName,
            stockCount: 0,
            changePercent: 0.0,
            upCount: 0,
            downCount: 0,
            flatCount: 0,
            portfolioWeight: 0.0,
            totalGainLoss: 0.0,
            totalGainLossPercent: 0.0,
            lastUpdated: Date()
        )
    }
}

/// 섹터별 성과 계산 유틸리티
class SectorPerformanceCalculator {

    /// 기존 StockModel.StockProfileData용 섹터별 성과 계산 (기존 UI 호환성 유지)
    /// - Parameter stocks: 계산할 주식 데이터 배열
    /// - Returns: 섹터별 성과 정보 딕셔너리
    static func calculateSectorPerformance(from stocks: [StockModel.StockProfileData]) -> [String: SectorPerformanceData] {
        // 기존 로직을 StockModel.StockProfileData에 맞게 구현
        let groupedStocks = Dictionary(grouping: stocks) { $0.sector.isEmpty ? "기타" : $0.sector }
        var sectorPerformances: [String: SectorPerformanceData] = [:]

        // 전체 포트폴리오 가치 계산 (비중 계산용)
        let totalPortfolioValue = calculateTotalPortfolioValueFromStockModel(from: stocks)

        for (sectorName, sectorStocks) in groupedStocks {
            let performance = calculateSectorPerformanceFromStockModel(
                sectorName: sectorName,
                stocks: sectorStocks,
                totalPortfolioValue: totalPortfolioValue
            )
            sectorPerformances[sectorName] = performance
        }

        return sectorPerformances
    }

    /// ChartView용 섹터별 성과 계산
    /// - Parameter stocks: 계산할 주식 데이터 배열
    /// - Returns: 섹터별 성과 정보 딕셔너리
    static func calculateSectorPerformance(from stocks: [ChartStockData]) -> [String: SectorPerformanceData] {
        let groupedStocks = Dictionary(grouping: stocks) { $0.sector.isEmpty ? "기타" : $0.sector }
        var sectorPerformances: [String: SectorPerformanceData] = [:]

        // 전체 포트폴리오 가치 계산 (비중 계산용)
        let totalPortfolioValue = calculateTotalPortfolioValue(from: stocks)

        for (sectorName, sectorStocks) in groupedStocks {
            let performance = calculateSectorPerformance(
                sectorName: sectorName,
                stocks: sectorStocks,
                totalPortfolioValue: totalPortfolioValue
            )
            sectorPerformances[sectorName] = performance
        }

        return sectorPerformances
    }

    /// 개별 섹터의 성과를 계산합니다
    private static func calculateSectorPerformance(
        sectorName: String,
        stocks: [ChartStockData],
        totalPortfolioValue: Double
    ) -> SectorPerformanceData {

        let stockCount = stocks.count
        var upCount = 0
        var downCount = 0
        var flatCount = 0

        var totalChangeWeighted = 0.0
        var totalMarketCap = 0.0
        var sectorPortfolioValue = 0.0
        var totalGainLoss = 0.0

        for stock in stocks {
            // 시세 정보 확인
            guard let quote = stock.quote else { continue }

            let changePercent = quote.changePercent
            let marketCap = stock.financials?.marketCap ?? 0.0

            // 상승/하락/보합 분류
            if changePercent > 0.01 {
                upCount += 1
            } else if changePercent < -0.01 {
                downCount += 1
            } else {
                flatCount += 1
            }

            // 시가총액 가중 평균을 위한 계산
            if marketCap > 0 {
                totalChangeWeighted += changePercent * marketCap
                totalMarketCap += marketCap
            }

            // 포지션 기반 수익률 계산
            let positionValue = calculatePositionValue(for: stock)
            sectorPortfolioValue += positionValue

            if !stock.positions.isEmpty {
                let stockPositions = convertToStockPositions(stock.positions)
                let gainLoss = PortfolioCalculator.calculateGainLoss(
                    from: stockPositions,
                    currentPrice: quote.currentPrice
                )
                totalGainLoss += gainLoss
            }
        }

        // 섹터 평균 변동률 계산 (시가총액 가중)
        let sectorChangePercent = totalMarketCap > 0 ? totalChangeWeighted / totalMarketCap : 0.0

        // 포트폴리오 비중 계산
        let portfolioWeight = totalPortfolioValue > 0 ? (sectorPortfolioValue / totalPortfolioValue) * 100 : 0.0

        // 섹터 전체 손익률 계산
        let totalCost = stocks.reduce(0.0) { sum, stock in
            let stockPositions = convertToStockPositions(stock.positions)
            return sum + PortfolioCalculator.calculateTotalCost(from: stockPositions)
        }
        let totalGainLossPercent = totalCost > 0 ? (totalGainLoss / totalCost) * 100 : 0.0

        return SectorPerformanceData(
            sectorName: sectorName,
            stockCount: stockCount,
            changePercent: sectorChangePercent,
            upCount: upCount,
            downCount: downCount,
            flatCount: flatCount,
            portfolioWeight: portfolioWeight,
            totalGainLoss: totalGainLoss,
            totalGainLossPercent: totalGainLossPercent,
            lastUpdated: Date()
        )
    }

    /// 전체 포트폴리오 가치 계산
    private static func calculateTotalPortfolioValue(from stocks: [ChartStockData]) -> Double {
        return stocks.reduce(0.0) { total, stock in
            total + calculatePositionValue(for: stock)
        }
    }

    /// 개별 주식의 포지션 가치 계산
    private static func calculatePositionValue(for stock: ChartStockData) -> Double {
        guard let quote = stock.quote, !stock.positions.isEmpty else { return 0.0 }

        let stockPositions = convertToStockPositions(stock.positions)
        return PortfolioCalculator.calculateCurrentValue(
            from: stockPositions,
            currentPrice: quote.currentPrice
        )
    }

    /// ChartPositionData를 StockPosition으로 변환
    private static func convertToStockPositions(_ positionData: [ChartPositionData]) -> [StockPosition] {
        return positionData.map { data in
            let position = StockPosition(
                symbol: data.symbol,
                pricePerShare: data.averagePrice,
                quantity: Double(data.shares),
                dateAdded: data.purchaseDate
            )
            return position
        }
    }

    // MARK: - StockModel.StockProfileData용 메서드들

    /// StockModel.StockProfileData용 개별 섹터 성과 계산
    private static func calculateSectorPerformanceFromStockModel(
        sectorName: String,
        stocks: [StockModel.StockProfileData],
        totalPortfolioValue: Double
    ) -> SectorPerformanceData {

        let stockCount = stocks.count
        var upCount = 0
        var downCount = 0
        var flatCount = 0

        var totalChangeWeighted = 0.0
        var totalMarketCap = 0.0
        var sectorPortfolioValue = 0.0
        var totalGainLoss = 0.0

        for stock in stocks {
            // 시세 정보 확인
            guard let quote = stock.quote else { continue }

            let changePercent = quote.changePercent
            let marketCap = stock.financials?.marketCap ?? 0.0

            // 상승/하락/보합 분류
            if changePercent > 0.01 {
                upCount += 1
            } else if changePercent < -0.01 {
                downCount += 1
            } else {
                flatCount += 1
            }

            // 시가총액 가중 평균을 위한 계산
            if marketCap > 0 {
                totalChangeWeighted += changePercent * marketCap
                totalMarketCap += marketCap
            }

            // 포지션 기반 수익률 계산
            let positionValue = calculatePositionValueFromStockModel(for: stock)
            sectorPortfolioValue += positionValue

            if !stock.positions.isEmpty {
                let gainLoss = PortfolioCalculator.calculateGainLoss(
                    from: stock.positions,
                    currentPrice: quote.currentPrice
                )
                totalGainLoss += gainLoss
            }
        }

        // 섹터 평균 변동률 계산 (시가총액 가중)
        let sectorChangePercent = totalMarketCap > 0 ? totalChangeWeighted / totalMarketCap : 0.0

        // 포트폴리오 비중 계산
        let portfolioWeight = totalPortfolioValue > 0 ? (sectorPortfolioValue / totalPortfolioValue) * 100 : 0.0

        // 섹터 전체 손익률 계산
        let totalCost = stocks.reduce(0.0) { sum, stock in
            sum + PortfolioCalculator.calculateTotalCost(from: stock.positions)
        }
        let totalGainLossPercent = totalCost > 0 ? (totalGainLoss / totalCost) * 100 : 0.0

        return SectorPerformanceData(
            sectorName: sectorName,
            stockCount: stockCount,
            changePercent: sectorChangePercent,
            upCount: upCount,
            downCount: downCount,
            flatCount: flatCount,
            portfolioWeight: portfolioWeight,
            totalGainLoss: totalGainLoss,
            totalGainLossPercent: totalGainLossPercent,
            lastUpdated: Date()
        )
    }

    /// StockModel.StockProfileData용 전체 포트폴리오 가치 계산
    private static func calculateTotalPortfolioValueFromStockModel(from stocks: [StockModel.StockProfileData]) -> Double {
        return stocks.reduce(0.0) { total, stock in
            total + calculatePositionValueFromStockModel(for: stock)
        }
    }

    /// StockModel.StockProfileData용 개별 주식의 포지션 가치 계산
    private static func calculatePositionValueFromStockModel(for stock: StockModel.StockProfileData) -> Double {
        guard let quote = stock.quote, !stock.positions.isEmpty else { return 0.0 }

        return PortfolioCalculator.calculateCurrentValue(
            from: stock.positions,
            currentPrice: quote.currentPrice
        )
    }
}

