//
//  ChartView.swift
//  Stocks
//
//  Created by oozoofrog on 7/23/25.
//

import SwiftUI
import Charts
import StockData

struct ChartView: View {
    @StateObject private var viewModel: ChartViewModel
    @Environment(StockEnvironment.self) private var stockEnvironment

    init(stockDataManager: StockDataManager? = nil) {
        if let dataManager = stockDataManager {
            self._viewModel = StateObject(wrappedValue: ChartViewModel(stockDataManager: dataManager))
        } else {
            // 프로덕션 환경에서는 Environment를 통해 주입받음
            self._viewModel = StateObject(wrappedValue: ChartViewModel(
                stockDataManager: try! StockEnvironment.production().dataManager
            ))
        }
    }

    var body: some View {
        NavigationView {
            ScrollView {
                LazyVStack(spacing: 20) {
                    // 포트폴리오 요약 섹션
                    PortfolioSummarySection()

                    // 섹터 비중 파이 차트
                    SectorAllocationSection()

                    // 섹터별 성과 바 차트
                    SectorPerformanceSection()

                    // 상위 종목 그리드
                    TopStocksSection()
                }
                .padding(.horizontal, 16)
                .padding(.top, 8)
            }
            .navigationTitle("포트폴리오 차트")
            .navigationBarTitleDisplayMode(.large)
            .refreshable {
                await viewModel.refreshData()
            }
        }
        .environmentObject(viewModel)
        .task {
            await viewModel.loadInitialData()
        }
    }
}

// MARK: - 포트폴리오 요약 섹션
private struct PortfolioSummarySection: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            HStack {
                Text("포트폴리오 요약")
                    .font(.title2)
                    .fontWeight(.bold)

                Spacer()

                if viewModel.isLoading {
                    ProgressView()
                        .scaleEffect(0.8)
                }
            }

            // 포트폴리오 요약 카드들
            PortfolioSummaryCardsView()
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }
}

// MARK: - 섹터 비중 섹션
private struct SectorAllocationSection: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            Text("섹터별 비중")
                .font(.title2)
                .fontWeight(.bold)

            if viewModel.sectorPerformances.isEmpty {
                // 빈 상태 표시
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color(.systemGroupedBackground))
                    .frame(height: 300)
                    .overlay(
                        VStack(spacing: 8) {
                            Image(systemName: "chart.pie")
                                .font(.system(size: 40))
                                .foregroundColor(.secondary)
                            Text("섹터 데이터가 없습니다")
                                .foregroundColor(.secondary)
                        }
                    )
            } else {
                // 파이 차트와 범례
                HStack(spacing: 16) {
                    // 파이 차트
                    SectorPieChartView()
                        .frame(width: 200, height: 200)

                    // 범례
                    SectorLegendView()
                        .frame(maxWidth: .infinity)
                }
                .frame(height: 220)
            }
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }
}

// MARK: - 섹터 파이 차트
private struct SectorPieChartView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    // 섹터별 색상 정의
    private let sectorColors: [String: Color] = [
        "Technology": .blue,
        "Healthcare": .green,
        "Financial Services": .orange,
        "Consumer Cyclical": .purple,
        "Communication Services": .pink,
        "Industrials": .brown,
        "Consumer Defensive": .cyan,
        "Energy": .red,
        "Utilities": .gray,
        "Real Estate": .indigo,
        "Materials": .mint,
        "기타": .secondary
    ]

    var body: some View {
        let chartData = prepareChartData()

        Chart(chartData, id: \.sector) { data in
            SectorMark(
                angle: .value("비중", data.weight),
                innerRadius: .ratio(0.4),
                angularInset: 2.0
            )
            .foregroundStyle(sectorColors[data.sector] ?? .gray)
            .opacity(0.8)
        }
        .chartLegend(.hidden)
    }

    private func prepareChartData() -> [SectorChartData] {
        return viewModel.sectorPerformances.map { (sector, performance) in
            SectorChartData(
                sector: sector,
                weight: max(performance.portfolioWeight, 0.1) // 최소 0.1%로 표시
            )
        }
        .sorted { $0.weight > $1.weight } // 큰 순서대로 정렬
    }
}

// MARK: - 섹터 범례
private struct SectorLegendView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    // 섹터별 색상 정의 (파이 차트와 동일)
    private let sectorColors: [String: Color] = [
        "Technology": .blue,
        "Healthcare": .green,
        "Financial Services": .orange,
        "Consumer Cyclical": .purple,
        "Communication Services": .pink,
        "Industrials": .brown,
        "Consumer Defensive": .cyan,
        "Energy": .red,
        "Utilities": .gray,
        "Real Estate": .indigo,
        "Materials": .mint,
        "기타": .secondary
    ]

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            let sortedSectors = viewModel.sectorPerformances
                .sorted { $0.value.portfolioWeight > $1.value.portfolioWeight }

            ForEach(Array(sortedSectors.enumerated()), id: \.element.key) { index, sector in
                HStack(spacing: 8) {
                    // 색상 인디케이터
                    Circle()
                        .fill(sectorColors[sector.key] ?? .gray)
                        .frame(width: 12, height: 12)

                    // 섹터명
                    Text(sector.key)
                        .font(.caption)
                        .lineLimit(1)

                    Spacer()

                    // 비중 퍼센트
                    Text("\(sector.value.portfolioWeight, specifier: "%.1f")%")
                        .font(.caption)
                        .fontWeight(.medium)
                        .foregroundColor(sectorColors[sector.key] ?? .gray)
                }
            }

            // 총합 표시
            Divider()

            HStack {
                Text("총 비중")
                    .font(.caption)
                    .fontWeight(.semibold)

                Spacer()

                Text("100.0%")
                    .font(.caption)
                    .fontWeight(.semibold)
            }
        }
    }
}

// MARK: - 섹터 차트 데이터 모델
private struct SectorChartData {
    let sector: String
    let weight: Double
}

// MARK: - 섹터 성과 섹션
private struct SectorPerformanceSection: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            Text("섹터별 성과")
                .font(.title2)
                .fontWeight(.bold)

            if viewModel.sectorPerformances.isEmpty {
                // 빈 상태 표시
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color(.systemGroupedBackground))
                    .frame(height: 250)
                    .overlay(
                        VStack(spacing: 8) {
                            Image(systemName: "chart.bar")
                                .font(.system(size: 40))
                                .foregroundColor(.secondary)
                            Text("섹터 성과 데이터가 없습니다")
                                .foregroundColor(.secondary)
                        }
                    )
            } else {
                // 섹터별 성과 바 차트
                SectorPerformanceBarChartView()
                    .frame(height: 250)
            }
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }
}

// MARK: - 상위 종목 섹션
private struct TopStocksSection: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            HStack {
                Text("상위 종목")
                    .font(.title2)
                    .fontWeight(.bold)

                Spacer()

                Text("Top \(min(viewModel.topStocks.count, 6))")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }

            if viewModel.topStocks.isEmpty {
                // 빈 상태 표시
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color(.systemGroupedBackground))
                    .frame(height: 200)
                    .overlay(
                        VStack(spacing: 8) {
                            Image(systemName: "chart.line.uptrend.xyaxis")
                                .font(.system(size: 40))
                                .foregroundColor(.secondary)
                            Text("상위 종목 데이터가 없습니다")
                                .foregroundColor(.secondary)
                        }
                    )
            } else {
                // 상위 종목 그리드
                TopStocksGridView()
            }
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }
}

// MARK: - 포트폴리오 요약 카드들
private struct PortfolioSummaryCardsView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        let summary = viewModel.portfolioSummary

        VStack(spacing: 12) {
            // 첫 번째 행: 총 가치와 총 손익
            HStack(spacing: 12) {
                SummaryCard(
                    title: "총 가치",
                    value: formatCurrency(summary.totalValue),
                    subtitle: "포트폴리오 전체",
                    color: .blue
                )

                SummaryCard(
                    title: "총 손익",
                    value: formatCurrency(summary.totalGainLoss),
                    subtitle: formatPercent(summary.totalGainLossPercent),
                    color: summary.totalGainLoss >= 0 ? .green : .red
                )
            }

            // 두 번째 행: 당일 변동
            HStack(spacing: 12) {
                SummaryCard(
                    title: "당일 변동",
                    value: formatCurrency(summary.dayChange),
                    subtitle: formatPercent(summary.dayChangePercent),
                    color: summary.dayChange >= 0 ? .green : .red
                )

                SummaryCard(
                    title: "업데이트",
                    value: formatTime(summary.lastUpdated),
                    subtitle: "마지막 갱신",
                    color: .gray
                )
            }
        }
    }

    private func formatCurrency(_ value: Double) -> String {
        let formatter = NumberFormatter()
        formatter.numberStyle = .currency
        formatter.currencyCode = "USD"
        formatter.maximumFractionDigits = 0
        return formatter.string(from: NSNumber(value: value)) ?? "$0"
    }

    private func formatPercent(_ value: Double) -> String {
        let formatter = NumberFormatter()
        formatter.numberStyle = .percent
        formatter.maximumFractionDigits = 2
        formatter.positivePrefix = "+"
        return formatter.string(from: NSNumber(value: value / 100)) ?? "0%"
    }

    private func formatTime(_ date: Date) -> String {
        let formatter = DateFormatter()
        formatter.timeStyle = .short
        return formatter.string(from: date)
    }
}

// MARK: - 요약 카드
private struct SummaryCard: View {
    let title: String
    let value: String
    let subtitle: String
    let color: Color

    var body: some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.caption)
                .foregroundColor(.secondary)

            Text(value)
                .font(.title3)
                .fontWeight(.semibold)
                .foregroundColor(color)
                .lineLimit(1)
                .minimumScaleFactor(0.8)

            Text(subtitle)
                .font(.caption)
                .foregroundColor(.secondary)
                .lineLimit(1)
        }
        .frame(maxWidth: .infinity, alignment: .leading)
        .padding()
        .background(Color(.systemBackground))
        .clipShape(RoundedRectangle(cornerRadius: 8))
        .shadow(color: .black.opacity(0.1), radius: 2, x: 0, y: 1)
    }
}

// MARK: - 섹터별 성과 바 차트
private struct SectorPerformanceBarChartView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    // 섹터별 색상 정의 (파이 차트와 동일)
    private let sectorColors: [String: Color] = [
        "Technology": .blue,
        "Healthcare": .green,
        "Financial Services": .orange,
        "Consumer Cyclical": .purple,
        "Communication Services": .pink,
        "Industrials": .brown,
        "Consumer Defensive": .cyan,
        "Energy": .red,
        "Utilities": .gray,
        "Real Estate": .indigo,
        "Materials": .mint,
        "기타": .secondary
    ]

    var body: some View {
        let chartData = prepareChartData()

        Chart(chartData, id: \.sector) { data in
            BarMark(
                x: .value("섹터", data.sector),
                y: .value("수익률", data.performance)
            )
            .foregroundStyle(getColor(for: data.performance, sector: data.sector))
            .opacity(0.8)
        }
        .chartXAxis {
            AxisMarks(preset: .aligned, position: .bottom) { value in
                AxisValueLabel {
                    if let sector = value.as(String.self) {
                        Text(abbreviateSectorName(sector))
                            .font(.caption2)
                            .lineLimit(1)
                    }
                }
            }
        }
        .chartYAxis {
            AxisMarks(preset: .extended, position: .leading) { value in
                AxisGridLine()
                AxisValueLabel {
                    if let performance = value.as(Double.self) {
                        Text("\(performance, specifier: "%.1f")%")
                            .font(.caption2)
                    }
                }
            }
        }
        .chartPlotStyle { plotArea in
            plotArea
                .background(Color(.systemBackground))
                .border(Color(.separator), width: 0.5)
        }
        .padding(.horizontal, 8)
    }

    private func prepareChartData() -> [SectorPerformanceChartData] {
        return viewModel.sectorPerformances.map { (sector, performance) in
            SectorPerformanceChartData(
                sector: sector,
                performance: performance.totalGainLossPercent
            )
        }
        .sorted { $0.performance > $1.performance } // 성과 순으로 정렬
    }

    private func getColor(for performance: Double, sector: String) -> Color {
        if performance > 0 {
            return .green
        } else if performance < 0 {
            return .red
        } else {
            return sectorColors[sector] ?? .gray
        }
    }

    private func abbreviateSectorName(_ sector: String) -> String {
        switch sector {
        case "Technology":
            return "Tech"
        case "Healthcare":
            return "Health"
        case "Financial Services":
            return "Finance"
        case "Consumer Cyclical":
            return "Cyclical"
        case "Communication Services":
            return "Comm"
        case "Industrials":
            return "Industry"
        case "Consumer Defensive":
            return "Defensive"
        case "Energy":
            return "Energy"
        case "Utilities":
            return "Utilities"
        case "Real Estate":
            return "RealEst"
        case "Materials":
            return "Materials"
        case "기타":
            return "기타"
        default:
            return String(sector.prefix(6))
        }
    }
}

// MARK: - 섹터 성과 차트 데이터 모델
private struct SectorPerformanceChartData {
    let sector: String
    let performance: Double
}

// MARK: - 상위 종목 그리드
private struct TopStocksGridView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    private let columns = [
        GridItem(.flexible(), spacing: 8),
        GridItem(.flexible(), spacing: 8),
        GridItem(.flexible(), spacing: 8)
    ]

    var body: some View {
        LazyVGrid(columns: columns, spacing: 12) {
            ForEach(Array(viewModel.topStocks.prefix(6).enumerated()), id: \.element.symbol) { index, stock in
                TopStockCard(stock: stock, rank: index + 1)
            }
        }
    }
}

// MARK: - 상위 종목 카드
private struct TopStockCard: View {
    let stock: ChartStockData
    let rank: Int

    var body: some View {
        VStack(alignment: .leading, spacing: 6) {
            // 헤더 (순위와 심볼)
            HStack {
                Text("#\(rank)")
                    .font(.caption2)
                    .fontWeight(.bold)
                    .foregroundColor(.secondary)

                Spacer()

                Text(stock.symbol)
                    .font(.caption)
                    .fontWeight(.semibold)
                    .lineLimit(1)
            }

            // 회사명
            Text(stock.companyName)
                .font(.caption2)
                .foregroundColor(.secondary)
                .lineLimit(1)
                .minimumScaleFactor(0.8)

            Spacer()

            // 가격 정보
            if let quote = stock.quote {
                VStack(alignment: .leading, spacing: 2) {
                    Text("$\(quote.currentPrice, specifier: "%.2f")")
                        .font(.title3)
                        .fontWeight(.semibold)
                        .lineLimit(1)
                        .minimumScaleFactor(0.8)

                    HStack(spacing: 4) {
                        Image(systemName: quote.changePercent >= 0 ? "arrow.up.right" : "arrow.down.right")
                            .font(.caption2)
                            .foregroundColor(quote.changePercent >= 0 ? .green : .red)

                        Text("\(quote.changePercent, specifier: "%.2f")%")
                            .font(.caption)
                            .fontWeight(.medium)
                            .foregroundColor(quote.changePercent >= 0 ? .green : .red)
                    }
                }
            } else {
                VStack(alignment: .leading, spacing: 2) {
                    Text("--")
                        .font(.title3)
                        .fontWeight(.semibold)
                        .foregroundColor(.secondary)

                    Text("--")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }
            }

            // 포지션 정보
            if !stock.positions.isEmpty {
                let totalShares = stock.positions.reduce(0) { $0 + $1.shares }

                Text("\(totalShares, specifier: "%.1f") 주")
                    .font(.caption2)
                    .foregroundColor(.secondary)
                    .lineLimit(1)
            }
        }
        .padding(8)
        .frame(maxWidth: .infinity, minHeight: 100, maxHeight: 120)
        .background(Color(.systemBackground))
        .clipShape(RoundedRectangle(cornerRadius: 8))
        .shadow(color: .black.opacity(0.1), radius: 1, x: 0, y: 1)
        .overlay(
            RoundedRectangle(cornerRadius: 8)
                .stroke(getRankColor(rank), lineWidth: 2)
                .opacity(0.3)
        )
    }

    private func getRankColor(_ rank: Int) -> Color {
        switch rank {
        case 1:
            return .yellow
        case 2:
            return .gray
        case 3:
            return .orange
        default:
            return .blue
        }
    }
}

#Preview {
    ChartView()
        .environment(try! StockEnvironment.production())
}

