    /// 종목 심볼
    let symbol: String

    /// 회사명
    let companyName: String

    /// 섹터
    let sector: String

    /// 산업
    let industry: String

    /// 현재 시세 정보
    var quote: ChartQuoteData?

    /// 보유 포지션 목록
    var positions: [ChartPositionData]

    /// 재무 정보
    var financials: ChartFinancialData?
}

/// 차트용 시세 데이터
struct ChartQuoteData {
    let symbol: String
    let currentPrice: Double
    let changeAmount: Double
    let changePercent: Double
    let volume: Int
    let avgVolume: Int
    let marketCap: Double
    let peRatio: Double
    let lastUpdated: Date
}

/// 차트용 포지션 데이터
struct ChartPositionData {
    let symbol: String
    let shares: Int
    let averagePrice: Double
    let purchaseDate: Date
}

/// 차트용 재무 데이터
struct ChartFinancialData {
    let symbol: String
    let marketCap: Double
    let enterpriseValue: Double
    let peRatio: Double
    let pegRatio: Double
    let bookValue: Double
    let priceToBook: Double
    let lastUpdated: Date
}

