            Text(stock.companyName)
                .font(.caption2)
                .foregroundColor(.secondary)
                .lineLimit(1)
                .minimumScaleFactor(0.8)

            Spacer()

            // 가격 정보
            if let quote = stock.quote {
                VStack(alignment: .leading, spacing: 2) {
                    Text("$\(quote.currentPrice, specifier: "%.2f")")
                        .font(.title3)
                        .fontWeight(.semibold)
                        .lineLimit(1)
