import Foundation

/// 포트폴리오 전체 요약 정보
struct PortfolioSummary {
    /// 포트폴리오 총 가치
    let totalValue: Double

    /// 총 손익 금액
    let totalGainLoss: Double

    /// 총 손익률 (%)
    let totalGainLossPercent: Double

    /// 당일 변동 금액
    let dayChange: Double

    /// 당일 변동률 (%)
    let dayChangePercent: Double

    /// 마지막 업데이트 시간
    let lastUpdated: Date

    /// 빈 데이터 생성자
    static func empty() -> PortfolioSummary {
        PortfolioSummary(
            totalValue: 0.0,
            totalGainLoss: 0.0,
            totalGainLossPercent: 0.0,
            dayChange: 0.0,
            dayChangePercent: 0.0,
            lastUpdated: Date()
        )
    }
}

/// 차트 설정 정보
struct ChartConfiguration {
    /// 데이터 새로고침 간격 (초)
    let refreshInterval: TimeInterval

    /// 애니메이션 지속 시간 (초)
    let animationDuration: Double

    /// 그리드에 표시할 최대 종목 수
    let maxItemsInGrid: Int

    /// 차트 색상 테마
    let chartColors: ChartColorTheme

    /// 기본 설정
    init(
        refreshInterval: TimeInterval = 30.0,
        animationDuration: Double = 0.6,
        maxItemsInGrid: Int = 8,
        chartColors: ChartColorTheme = ChartColorTheme()
    ) {
        self.refreshInterval = refreshInterval
        self.animationDuration = animationDuration
        self.maxItemsInGrid = maxItemsInGrid
        self.chartColors = chartColors
    }

    /// 테스트용 설정 (자동 새로고침 비활성화)
    static var testConfiguration: ChartConfiguration {
        ChartConfiguration(refreshInterval: 0)
    }
}

/// 차트 색상 테마
struct ChartColorTheme {
    let bullish: String = "green"
    let bearish: String = "red"
    let neutral: String = "gray"
    let background: String = "chartBackground"
}

