                .foregroundStyle(sectorColors[data.sector] ?? .gray)
                .opacity(selectedSector == nil || selectedSector == data.sector ? 1.0 : 0.6)
                .cornerRadius(4)
            }
            .chartAngleSelection(value: .constant(nil))
            .chartBackground { chartProxy in
                GeometryReader { geometry in
                    let frame = geometry.frame(in: .local)
                    VStack {
                        if let selectedSector = selectedSector,
                           let performance = viewModel.sectorPerformances[selectedSector] {
                            // 선택된 섹터 정보 표시
                            VStack(spacing: 4) {
                                Text(selectedSector)
                                    .font(.caption)
                                    .fontWeight(.semibold)

                                Text("\(performance.portfolioWeight, specifier: "%.1f")%")
                                    .font(.title3)
                                    .fontWeight(.bold)
                                    .foregroundColor(sectorColors[selectedSector] ?? .gray)

                                Text("\(performance.stockCount)개 종목")
                                    .font(.caption2)
                                    .foregroundColor(.secondary)
                            }
                            .padding(8)
                            .background(Color(.systemBackground).opacity(0.9))
                            .clipShape(RoundedRectangle(cornerRadius: 8))
                            .shadow(radius: 2)
                        } else {
                            // 전체 포트폴리오 정보
                            VStack(spacing: 4) {
                                Text("전체")
                                    .font(.caption)
                                    .fontWeight(.semibold)

                                Text("100%")
                                    .font(.title3)
                                    .fontWeight(.bold)

                                Text("\(viewModel.topStocks.count)개 종목")
                                    .font(.caption2)
                                    .foregroundColor(.secondary)
                            }
                        }
                    }
                    .position(x: frame.width / 2, y: frame.height / 2)
                }
            }
            .frame(width: 240, height: 240)
            .onAppear {
                withAnimation(.easeInOut(duration: 1.0)) {
                    animationProgress = 1.0
                }
            }

            // 터치 영역
            GeometryReader { geometry in
                ForEach(chartData, id: \.sector) { data in
                    PieSliceTouchArea(
                        data: data,
                        totalValue: chartData.reduce(0) { $0 + $1.weight },
                        innerRadius: 0.45,
                        outerRadius: 1.0,
                        geometry: geometry
                    )
                    .onTapGesture {
                        withAnimation(.easeInOut(duration: 0.3)) {
                            if selectedSector == data.sector {
                                selectedSector = nil
                            } else {
                                selectedSector = data.sector
                                // 햅틱 피드백
                                let impact = UIImpactFeedbackGenerator(style: .light)
                                impact.impactOccurred()
                            }
                        }
                    }
                }
            }
            .frame(width: 240, height: 240)
        }
        .sheet(isPresented: $showingDetail) {
            if let selectedSector = selectedSector {
                SectorDetailView(sectorName: selectedSector)
                    .environmentObject(viewModel)
            }
        }
    }

    private func prepareChartData() -> [SectorChartData] {
        return viewModel.sectorPerformances.map { (sector, performance) in
            SectorChartData(
                sector: sector,
                weight: max(performance.portfolioWeight, 0.5) // 최소 0.5%로 표시
            )
        }
        .sorted { $0.weight > $1.weight }
    }
}

/// 파이 차트 터치 영역 계산
struct PieSliceTouchArea: View {
    let data: SectorChartData
    let totalValue: Double
    let innerRadius: Double
    let outerRadius: Double
    let geometry: GeometryProxy

    var body: some View {
        Path { path in
            let center = CGPoint(x: geometry.size.width / 2, y: geometry.size.height / 2)
            let radius = min(geometry.size.width, geometry.size.height) / 2

            // 각도 계산
            let percentage = data.weight / totalValue
            let startAngle = calculateStartAngle()
            let endAngle = startAngle + (percentage * 360)

            // 파이 슬라이스 경로 생성
            path.move(to: center)
            path.addArc(
                center: center,
                radius: CGFloat(radius * outerRadius),
                startAngle: .degrees(startAngle - 90),
                endAngle: .degrees(endAngle - 90),
                clockwise: false
            )
            path.closeSubpath()
        }
        .fill(Color.clear)
        .contentShape(Path { path in
            let center = CGPoint(x: geometry.size.width / 2, y: geometry.size.height / 2)
            let radius = min(geometry.size.width, geometry.size.height) / 2

            let percentage = data.weight / totalValue
            let startAngle = calculateStartAngle()
            let endAngle = startAngle + (percentage * 360)

            path.move(to: center)
            path.addArc(
                center: center,
                radius: CGFloat(radius * outerRadius),
                startAngle: .degrees(startAngle - 90),
                endAngle: .degrees(endAngle - 90),
                clockwise: false
            )
            path.closeSubpath()
        })
    }

    private func calculateStartAngle() -> Double {
        // 이전 섹터들의 각도 합계 계산
        // 실제 구현에서는 데이터 정렬 순서에 따라 계산 필요
        return 0
    }
}

/// 섹터 상세 정보 뷰
struct SectorDetailView: View {
    let sectorName: String
    @EnvironmentObject private var viewModel: ChartViewModel
    @Environment(\.dismiss) private var dismiss

    var body: some View {
        NavigationView {
            if let performance = viewModel.sectorPerformances[sectorName] {
                ScrollView {
                    VStack(alignment: .leading, spacing: 20) {
                        // 섹터 요약
                        SectorSummaryCard(sectorName: sectorName, performance: performance)

                        // 섹터 내 종목 목록
                        SectorStocksList(sectorName: sectorName)
                            .environmentObject(viewModel)
                    }
                    .padding()
                }
                .navigationTitle(sectorName)
                .navigationBarTitleDisplayMode(.large)
                .toolbar {
                    ToolbarItem(placement: .navigationBarTrailing) {
                        Button("닫기") {
                            dismiss()
                        }
                    }
                }
            }
        }
    }
}

/// 섹터 요약 카드
struct SectorSummaryCard: View {
    let sectorName: String
    let performance: SectorPerformanceData

    var body: some View {
        VStack(spacing: 16) {
            // 헤더
            HStack {
                Text("섹터 성과")
                    .font(.headline)
                Spacer()
                Image(systemName: "chart.pie")
                    .foregroundColor(.secondary)
            }

            // 메트릭스 그리드
            LazyVGrid(columns: [
                GridItem(.flexible()),
                GridItem(.flexible())
            ], spacing: 12) {
                MetricCard(
                    title: "포트폴리오 비중",
                    value: "\(performance.portfolioWeight, specifier: "%.1f")%",
                    color: .blue
                )

                MetricCard(
                    title: "종목 수",
                    value: "\(performance.stockCount)개",
                    color: .purple
                )

                MetricCard(
                    title: "일일 변동",
                    value: "\(performance.changePercent, specifier: "%.2f")%",
                    color: performance.changePercent >= 0 ? .green : .red
                )

                MetricCard(
                    title: "총 손익",
                    value: formatCurrency(performance.totalGainLoss),
                    color: performance.totalGainLoss >= 0 ? .green : .red
                )
            }

            // 상승/하락 분포
            HStack(spacing: 4) {
                Label("\(performance.upCount)", systemImage: "arrow.up")
                    .foregroundColor(.green)
                    .font(.caption)

                Spacer()

                Label("\(performance.flatCount)", systemImage: "minus")
                    .foregroundColor(.gray)
                    .font(.caption)

                Spacer()

                Label("\(performance.downCount)", systemImage: "arrow.down")
                    .foregroundColor(.red)
                    .font(.caption)
            }
            .padding(.horizontal)
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }

    private func formatCurrency(_ value: Double) -> String {
        let formatter = NumberFormatter()
        formatter.numberStyle = .currency
        formatter.currencyCode = "USD"
        formatter.maximumFractionDigits = 0
        return formatter.string(from: NSNumber(value: value)) ?? "$0"
    }
}

/// 메트릭 카드
struct MetricCard: View {
    let title: String
    let value: String
    let color: Color

    var body: some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.caption)
                .foregroundColor(.secondary)

            Text(value)
                .font(.title3)
                .fontWeight(.semibold)
                .foregroundColor(color)
        }
        .frame(maxWidth: .infinity, alignment: .leading)
        .padding()
        .background(Color(.systemBackground))
        .clipShape(RoundedRectangle(cornerRadius: 8))
    }
}

/// 섹터 내 종목 목록
struct SectorStocksList: View {
    let sectorName: String
    @EnvironmentObject private var viewModel: ChartViewModel

    var sectorStocks: [ChartStockData] {
        viewModel.topStocks.filter { $0.sector == sectorName }
    }

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            Text("섹터 내 종목")
                .font(.headline)

            if sectorStocks.isEmpty {
                Text("이 섹터에 속한 종목이 없습니다.")
                    .foregroundColor(.secondary)
                    .padding()
            } else {
                ForEach(sectorStocks, id: \.symbol) { stock in
                    StockRowCard(stock: stock)
                }
            }
        }
    }
}

/// 주식 행 카드
struct StockRowCard: View {
    let stock: ChartStockData

    var body: some View {
        HStack {
            VStack(alignment: .leading, spacing: 4) {
                Text(stock.symbol)
                    .font(.headline)

                Text(stock.companyName)
                    .font(.caption)
                    .foregroundColor(.secondary)
                    .lineLimit(1)
            }

            Spacer()

            if let quote = stock.quote {
                VStack(alignment: .trailing, spacing: 4) {
                    Text("$\(quote.currentPrice, specifier: "%.2f")")
                        .font(.headline)

                    HStack(spacing: 4) {
                        Image(systemName: quote.changePercent >= 0 ? "arrow.up.right" : "arrow.down.right")
                            .font(.caption)

                        Text("\(quote.changePercent, specifier: "%.2f")%")
                            .font(.caption)
                            .fontWeight(.medium)
                    }
                    .foregroundColor(quote.changePercent >= 0 ? .green : .red)
                }
            }
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 8))
    }
}

