//
//  AnimatedBarChartView.swift
//  Stocks
//
//  Created by oozoofrog on 7/25/25.
//

import SwiftUI
import Charts

/// 애니메이션이 있는 바 차트 뷰
struct AnimatedBarChartView: View {
    @EnvironmentObject private var viewModel: ChartViewModel
    @State private var animationProgress: Double = 0
    @State private var selectedSector: String? = nil
    @State private var showTooltip = false
    @State private var tooltipPosition: CGPoint = .zero

    /// 섹터별 색상 정의 (파이 차트와 동일)
    private let sectorColors: [String: Color] = [
        "Technology": .blue,
        "Healthcare": .green,
        "Financial Services": .orange,
        "Consumer Cyclical": .purple,
        "Communication Services": .pink,
        "Industrials": .brown,
        "Consumer Defensive": .cyan,
        "Energy": .red,
        "Utilities": .gray,
        "Real Estate": .indigo,
        "Materials": .mint,
        "기타": .secondary
    ]

    var body: some View {
        let chartData = prepareChartData()

        Chart(chartData, id: \.sector) { data in
            BarMark(
                x: .value("섹터", data.sector),
                y: .value("수익률", data.performance * animationProgress)
            )
            .foregroundStyle(by: .value("성과", data.performance > 0 ? "상승" : "하락"))
            .cornerRadius(4)
            .opacity(selectedSector == nil || selectedSector == data.sector ? 1.0 : 0.4)
        }
        .chartForegroundStyleScale([
            "상승": Color.green,
            "하락": Color.red
        ])
        .chartXAxis {
            AxisMarks(preset: .aligned, position: .bottom) { value in
                AxisValueLabel {
                    if let sector = value.as(String.self) {
                        Text(abbreviateSectorName(sector))
                            .font(.caption2)
                            .lineLimit(1)
                            .rotationEffect(.degrees(-45))
                            .offset(y: 5)
                    }
                }
            }
        }
        .chartYAxis {
            AxisMarks(preset: .extended, position: .leading) { value in
                AxisGridLine(stroke: StrokeStyle(lineWidth: 0.5, dash: [5]))
                    .foregroundStyle(Color(.separator).opacity(0.5))
                AxisValueLabel {
                    if let performance = value.as(Double.self) {
                        Text("\(performance, specifier: "%.1f")%")
                            .font(.caption2)
                    }
                }
            }
        }
        .chartPlotStyle { plotArea in
            plotArea
                .background(
                    LinearGradient(
                        gradient: Gradient(colors: [
                            Color(.systemBackground),
                            Color(.systemBackground).opacity(0.95)
                        ]),
                        startPoint: .top,
                        endPoint: .bottom
                    )
                )
                .overlay(
                    RoundedRectangle(cornerRadius: 8)
                        .stroke(Color(.separator), lineWidth: 0.5)
                )
        }
        .frame(height: 250)
        .padding(.horizontal, 8)
        .onAppear {
            withAnimation(.spring(response: 0.8, dampingFraction: 0.8)) {
                animationProgress = 1.0
            }
        }
        .overlay(alignment: .topLeading) {
            if showTooltip, let selectedSector = selectedSector,
               let performance = viewModel.sectorPerformances[selectedSector] {
                SectorTooltipView(
                    sectorName: selectedSector,
                    performance: performance
                )
                .position(tooltipPosition)
                .transition(.scale.combined(with: .opacity))
            }
        }
        .onTapGesture { location in
            // 터치 위치를 기반으로 섹터 선택
            handleTapGesture(at: location, chartData: chartData)
        }
    }

    private func prepareChartData() -> [SectorPerformanceChartData] {
        return viewModel.sectorPerformances.map { (sector, performance) in
            SectorPerformanceChartData(
                sector: sector,
                performance: performance.totalGainLossPercent
            )
        }
        .sorted { $0.performance > $1.performance }
    }

    private func abbreviateSectorName(_ sector: String) -> String {
        switch sector {
        case "Technology":
            return "Tech"
        case "Healthcare":
            return "Health"
        case "Financial Services":
            return "Finance"
        case "Consumer Cyclical":
            return "Cyclical"
        case "Communication Services":
            return "Comm"
        case "Industrials":
            return "Industry"
        case "Consumer Defensive":
            return "Defensive"
        case "Energy":
            return "Energy"
        case "Utilities":
            return "Utilities"
        case "Real Estate":
            return "RealEst"
        case "Materials":
            return "Materials"
        case "기타":
            return "기타"
        default:
            return String(sector.prefix(6))
        }
    }

    private func handleTapGesture(at location: CGPoint, chartData: [SectorPerformanceChartData]) {
        // 간단한 터치 처리 로직
        withAnimation(.easeInOut(duration: 0.3)) {
            if selectedSector != nil {
                selectedSector = nil
                showTooltip = false
            } else {
                // 실제 구현에서는 차트 좌표계를 사용하여 정확한 섹터 선택
                tooltipPosition = location
                showTooltip = true

                // 햅틱 피드백
                let impact = UIImpactFeedbackGenerator(style: .light)
                impact.impactOccurred()
            }
        }
    }
}

/// 섹터 성과 차트 데이터 모델
struct SectorPerformanceChartData {
    let sector: String
    let performance: Double
}

/// 섹터 툴팁 뷰
struct SectorTooltipView: View {
    let sectorName: String
    let performance: SectorPerformanceData

    var body: some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(sectorName)
                .font(.caption)
                .fontWeight(.semibold)

            HStack(spacing: 8) {
                VStack(alignment: .leading, spacing: 2) {
                    Text("수익률")
                        .font(.caption2)
                        .foregroundColor(.secondary)
                    Text("\(performance.totalGainLossPercent, specifier: "%.2f")%")
                        .font(.caption)
                        .fontWeight(.medium)
                        .foregroundColor(performance.totalGainLossPercent >= 0 ? .green : .red)
                }

                VStack(alignment: .leading, spacing: 2) {
                    Text("종목 수")
                        .font(.caption2)
                        .foregroundColor(.secondary)
                    Text("\(performance.stockCount)개")
                        .font(.caption)
                        .fontWeight(.medium)
                }
            }
        }
        .padding(8)
        .background(
            RoundedRectangle(cornerRadius: 8)
                .fill(Color(.systemBackground))
                .shadow(color: .black.opacity(0.2), radius: 4, x: 0, y: 2)
        )
    }
}

/// 개선된 상위 종목 그리드 뷰
struct EnhancedTopStocksGridView: View {
    @EnvironmentObject private var viewModel: ChartViewModel
    @State private var selectedStock: ChartStockData? = nil

    private let columns = [
        GridItem(.flexible(), spacing: 12),
        GridItem(.flexible(), spacing: 12)
    ]

    var body: some View {
        LazyVGrid(columns: columns, spacing: 12) {
            ForEach(Array(viewModel.topStocks.prefix(6).enumerated()), id: \.element.symbol) { index, stock in
                EnhancedStockCard(
                    stock: stock,
                    rank: index + 1,
                    isSelected: selectedStock?.symbol == stock.symbol
                )
                .onTapGesture {
                    withAnimation(.spring(response: 0.3, dampingFraction: 0.8)) {
                        if selectedStock?.symbol == stock.symbol {
                            selectedStock = nil
                        } else {
                            selectedStock = stock
                            // 햅틱 피드백
                            let impact = UIImpactFeedbackGenerator(style: .light)
                            impact.impactOccurred()
                        }
                    }
                }
            }
        }
    }
}

/// 개선된 상위 종목 카드
struct EnhancedStockCard: View {
    let stock: ChartStockData
    let rank: Int
    let isSelected: Bool

    @State private var isHovered = false

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            // 헤더
            HStack {
                // 순위 배지
                Text("#\(rank)")
                    .font(.caption)
                    .fontWeight(.bold)
                    .foregroundColor(getRankColor(rank))
                    .padding(.horizontal, 6)
                    .padding(.vertical, 2)
                    .background(
                        Capsule()
                            .fill(getRankColor(rank).opacity(0.2))
                    )

                Spacer()

                Text(stock.symbol)
                    .font(.caption)
                    .fontWeight(.semibold)
                    .lineLimit(1)
            }

            // 회사명
            Text(stock.companyName)
                .font(.caption2)
                .foregroundColor(.secondary)
                .lineLimit(1)
                .minimumScaleFactor(0.8)

            Spacer()

            // 가격 정보
            if let quote = stock.quote {
                VStack(alignment: .leading, spacing: 4) {
                    Text("$\(quote.currentPrice, specifier: "%.2f")")
                        .font(.title3)
                        .fontWeight(.bold)
                        .lineLimit(1)
                        .minimumScaleFactor(0.8)

                    HStack(spacing: 4) {
                        Image(systemName: quote.changePercent >= 0 ? "arrow.up.right" : "arrow.down.right")
                            .font(.caption2)

                        Text("\(abs(quote.changePercent), specifier: "%.2f")%")
                            .font(.caption)
                            .fontWeight(.medium)
                    }
                    .foregroundColor(quote.changePercent >= 0 ? .green : .red)
                }

                // 포지션 정보
                if !stock.positions.isEmpty {
                    let totalShares = stock.positions.reduce(0) { $0 + $1.shares }
                    let totalValue = Double(totalShares) * quote.currentPrice

                    HStack(spacing: 4) {
                        Image(systemName: "bitcoinsign.circle")
                            .font(.caption2)
                            .foregroundColor(.secondary)

                        Text("$\(totalValue, specifier: "%.0f")")
                            .font(.caption2)
                            .foregroundColor(.secondary)
                            .lineLimit(1)
                    }
                }
            }
        }
        .padding(12)
        .frame(maxWidth: .infinity, minHeight: 120)
        .background(
            RoundedRectangle(cornerRadius: 12)
                .fill(Color(.systemBackground))
                .shadow(
                    color: .black.opacity(isSelected ? 0.15 : 0.1),
                    radius: isSelected ? 8 : 4,
                    x: 0,
                    y: isSelected ? 4 : 2
                )
        )
        .overlay(
            RoundedRectangle(cornerRadius: 12)
                .stroke(
                    isSelected ? getRankColor(rank) : Color.clear,
                    lineWidth: 2
                )
        )
        .scaleEffect(isSelected ? 1.05 : 1.0)
    }

    private func getRankColor(_ rank: Int) -> Color {
        switch rank {
        case 1:
            return .yellow
        case 2:
            return .gray
        case 3:
            return .orange
        default:
            return .blue
        }
    }
}

