//
//  ChartView.swift
//  Stocks
//
//  Created by oozoofrog on 7/23/25.
//

import SwiftUI
import Charts
import StockData

struct ChartView: View {
    @StateObject private var viewModel: ChartViewModel
    @Environment(StockEnvironment.self) private var stockEnvironment

    init(stockDataManager: StockDataManager? = nil) {
        if let dataManager = stockDataManager {
            self._viewModel = StateObject(wrappedValue: ChartViewModel(stockDataManager: dataManager))
        } else {
            // 프로덕션 환경에서는 Environment를 통해 주입받음
            self._viewModel = StateObject(wrappedValue: ChartViewModel(
                stockDataManager: try! StockEnvironment.production().dataManager
            ))
        }
    }

    var body: some View {
        NavigationView {
            ScrollView {
                LazyVStack(spacing: 20) {
                    // 포트폴리오 요약 섹션
                    PortfolioSummarySection()

                    // 섹터 비중 파이 차트
                    SectorAllocationSection()

                    // 섹터별 성과 바 차트
                    SectorPerformanceSection()

                    // 상위 종목 그리드
                    TopStocksSection()
                }
                .padding(.horizontal, 16)
                .padding(.top, 8)
            }
            .navigationTitle("포트폴리오 차트")
            .navigationBarTitleDisplayMode(.large)
            .refreshable {
                await viewModel.refreshData()
            }
        }
        .environmentObject(viewModel)
        .task {
            await viewModel.loadInitialData()
        }
    }
}

// MARK: - 포트폴리오 요약 섹션
private struct PortfolioSummarySection: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            HStack {
                Text("포트폴리오 요약")
                    .font(.title2)
                    .fontWeight(.bold)

                Spacer()

                if viewModel.isLoading {
                    ProgressView()
                        .scaleEffect(0.8)
                }
            }

            // 포트폴리오 요약 카드들
            PortfolioSummaryCardsView()
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }
}

// MARK: - 섹터 비중 섹션
private struct SectorAllocationSection: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            Text("섹터별 비중")
                .font(.title2)
                .fontWeight(.bold)

            if viewModel.sectorPerformances.isEmpty {
                // 빈 상태 표시
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color(.systemGroupedBackground))
                    .frame(height: 300)
                    .overlay(
                        VStack(spacing: 8) {
                            Image(systemName: "chart.pie")
                                .font(.system(size: 40))
                                .foregroundColor(.secondary)
                            Text("섹터 데이터가 없습니다")
                                .foregroundColor(.secondary)
                        }
                    )
            } else {
                // 파이 차트와 범례
                VStack(spacing: 20) {
                    // 애니메이션 파이 차트
                    SectorPieChartView()
                        .frame(height: 240)

                    // 범례
                    SectorLegendView()
                }
                .padding(.vertical, 8)
            }
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }
}

// MARK: - 섹터 파이 차트
private struct SectorPieChartView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        AnimatedPieChartView()
            .environmentObject(viewModel)
    }
}

// MARK: - 섹터 범례
private struct SectorLegendView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    // 섹터별 색상 정의 (파이 차트와 동일)
    private let sectorColors: [String: Color] = [
        "Technology": .blue,
        "Healthcare": .green,
        "Financial Services": .orange,
        "Consumer Cyclical": .purple,
        "Communication Services": .pink,
        "Industrials": .brown,
        "Consumer Defensive": .cyan,
        "Energy": .red,
        "Utilities": .gray,
        "Real Estate": .indigo,
        "Materials": .mint,
        "기타": .secondary
    ]

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            let sortedSectors = viewModel.sectorPerformances
                .sorted { $0.value.portfolioWeight > $1.value.portfolioWeight }

            ForEach(Array(sortedSectors.enumerated()), id: \.element.key) { index, sector in
                HStack(spacing: 8) {
                    // 색상 인디케이터
                    Circle()
                        .fill(sectorColors[sector.key] ?? .gray)
                        .frame(width: 12, height: 12)

                    // 섹터명
                    Text(sector.key)
                        .font(.caption)
                        .lineLimit(1)

                    Spacer()

                    // 비중 퍼센트
                    Text("\(sector.value.portfolioWeight, specifier: "%.1f")%")
                        .font(.caption)
                        .fontWeight(.medium)
                        .foregroundColor(sectorColors[sector.key] ?? .gray)
                }
            }

            // 총합 표시
            Divider()

            HStack {
                Text("총 비중")
                    .font(.caption)
                    .fontWeight(.semibold)

                Spacer()

                Text("100.0%")
                    .font(.caption)
                    .fontWeight(.semibold)
            }
        }
    }
}


// MARK: - 섹터 성과 섹션
private struct SectorPerformanceSection: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            Text("섹터별 성과")
                .font(.title2)
                .fontWeight(.bold)

            if viewModel.sectorPerformances.isEmpty {
                // 빈 상태 표시
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color(.systemGroupedBackground))
                    .frame(height: 250)
                    .overlay(
                        VStack(spacing: 8) {
                            Image(systemName: "chart.bar")
                                .font(.system(size: 40))
                                .foregroundColor(.secondary)
                            Text("섹터 성과 데이터가 없습니다")
                                .foregroundColor(.secondary)
                        }
                    )
            } else {
                // 섹터별 성과 바 차트
                SectorPerformanceBarChartView()
                    .frame(height: 250)
            }
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }
}

// MARK: - 상위 종목 섹션
private struct TopStocksSection: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            HStack {
                Text("상위 종목")
                    .font(.title2)
                    .fontWeight(.bold)

                Spacer()

                Text("Top \(min(viewModel.topStocks.count, 6))")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }

            if viewModel.topStocks.isEmpty {
                // 빈 상태 표시
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color(.systemGroupedBackground))
                    .frame(height: 200)
                    .overlay(
                        VStack(spacing: 8) {
                            Image(systemName: "chart.line.uptrend.xyaxis")
                                .font(.system(size: 40))
                                .foregroundColor(.secondary)
                            Text("상위 종목 데이터가 없습니다")
                                .foregroundColor(.secondary)
                        }
                    )
            } else {
                // 상위 종목 그리드
                TopStocksGridView()
            }
        }
        .padding()
        .background(Color(.systemGroupedBackground))
        .clipShape(RoundedRectangle(cornerRadius: 12))
    }
}

// MARK: - 포트폴리오 요약 카드들
private struct PortfolioSummaryCardsView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        let summary = viewModel.portfolioSummary

        VStack(spacing: 12) {
            // 첫 번째 행: 총 가치와 총 손익
            HStack(spacing: 12) {
                SummaryCard(
                    title: "총 가치",
                    value: formatCurrency(summary.totalValue),
                    subtitle: "포트폴리오 전체",
                    color: .blue
                )

                SummaryCard(
                    title: "총 손익",
                    value: formatCurrency(summary.totalGainLoss),
                    subtitle: formatPercent(summary.totalGainLossPercent),
                    color: summary.totalGainLoss >= 0 ? .green : .red
                )
            }

            // 두 번째 행: 당일 변동
            HStack(spacing: 12) {
                SummaryCard(
                    title: "당일 변동",
                    value: formatCurrency(summary.dayChange),
                    subtitle: formatPercent(summary.dayChangePercent),
                    color: summary.dayChange >= 0 ? .green : .red
                )

                SummaryCard(
                    title: "업데이트",
                    value: formatTime(summary.lastUpdated),
                    subtitle: "마지막 갱신",
                    color: .gray
                )
            }
        }
    }

    private func formatCurrency(_ value: Double) -> String {
        let formatter = NumberFormatter()
        formatter.numberStyle = .currency
        formatter.currencyCode = "USD"
        formatter.maximumFractionDigits = 0
        return formatter.string(from: NSNumber(value: value)) ?? "$0"
    }

    private func formatPercent(_ value: Double) -> String {
        let formatter = NumberFormatter()
        formatter.numberStyle = .percent
        formatter.maximumFractionDigits = 2
        formatter.positivePrefix = "+"
        return formatter.string(from: NSNumber(value: value / 100)) ?? "0%"
    }

    private func formatTime(_ date: Date) -> String {
        let formatter = DateFormatter()
        formatter.timeStyle = .short
        return formatter.string(from: date)
    }
}

// MARK: - 요약 카드
private struct SummaryCard: View {
    let title: String
    let value: String
    let subtitle: String
    let color: Color

    var body: some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.caption)
                .foregroundColor(.secondary)

            Text(value)
                .font(.title3)
                .fontWeight(.semibold)
                .foregroundColor(color)
                .lineLimit(1)
                .minimumScaleFactor(0.8)

            Text(subtitle)
                .font(.caption)
                .foregroundColor(.secondary)
                .lineLimit(1)
        }
        .frame(maxWidth: .infinity, alignment: .leading)
        .padding()
        .background(Color(.systemBackground))
        .clipShape(RoundedRectangle(cornerRadius: 8))
        .shadow(color: .black.opacity(0.1), radius: 2, x: 0, y: 1)
    }
}

// MARK: - 섹터별 성과 바 차트
private struct SectorPerformanceBarChartView: View {
    @EnvironmentObject private var viewModel: ChartViewModel

    var body: some View {
        AnimatedBarChartView()
            .environmentObject(viewModel)
    }
}


