@MainActor
@Observable
final class StockProfileRowViewModel {
    private enum Constants {
        static let itemIDPrefix = "StockProfileRow_"
    }

    private let symbol: String
    private let stockAPI: YahooFinanceClient
    private let dataManager: StockDataManager

    // ë·° ì‹ë³„ìž
    let itemID: String

    // ë¡œë”© ìƒíƒœ
    private(set) var isRefreshing: Bool = false

    // ì „ì—­ ê°€ê²© ì„œë¹„ìŠ¤ (ì£¼ìž…ë°›ì„ ì˜ˆì •)
    private var priceService: StockPriceService?

    // ë™ì‹œì„± ì œì–´ë¥¼ ìœ„í•œ Task
    private var refreshTask: Task<Void, Never>?

    // ìŠ¤ì¼ˆë ˆí†¤ í‘œì‹œ ì—¬ë¶€ ê²°ì •
    func shouldShowSkeleton(quote: StockQuoteData?) -> Bool {
        quote == nil || priceService?.isLoading(symbol: symbol) ?? false
    }

    // ì—ëŸ¬ ìƒíƒœ
    private(set) var lastError: Error?

    init(symbol: String, stockAPI: YahooFinanceClient, dataManager: StockDataManager) {
        self.symbol = symbol
        self.stockAPI = stockAPI
        self.dataManager = dataManager
        self.itemID = "\(Constants.itemIDPrefix)\(symbol)"

        // ì „ì—­ ê°€ê²© ì„œë¹„ìŠ¤ëŠ” ë‚˜ì¤‘ì— ì„¤ì •ë¨
        // TODO: StockEnvironmentì—ì„œ priceService ì£¼ìž…
    }

    /// ëª¨ë“  ë°ì´í„° ìƒˆë¡œê³ ì¹¨
    func refreshAllData() async {
        // ì´ì „ íƒœìŠ¤í¬ ì·¨ì†Œ
        refreshTask?.cancel()

        refreshTask = Task {
            guard !Task.isCancelled else { return }

            isRefreshing = true
            lastError = nil

            defer {
                isRefreshing = false
            }

            do {
                // Task ì·¨ì†Œ í™•ì¸
                try Task.checkCancellation()

                // Summary ë°ì´í„° ìš”ì²­ (ê°€ê²© ë°ì´í„°ëŠ” ì „ì—­ ì„œë¹„ìŠ¤ì—ì„œ ìžë™ ì²˜ë¦¬ë¨)
                let summaryResponse = try await stockAPI.getQuoteSummary(
                    symbol: symbol,
                    modules: [.summaryProfile, .summaryDetail]
                )

