import SwiftUI
import StockData
import StockModel
import Observation

struct StockProfileRowDetailView: View {
    @Environment(StockEnvironment.self) private var stockEnvironment
    @State private var showDeleteAllAlert = false

    let profile: StockProfileData
    let details: StockProfileDetails?
    let financials: StockDetailData?
    let quote: StockQuoteData?

    var body: some View {
        VStack(alignment: .leading, spacing: 16) {
            // Industry info
            if !profile.industry.isEmpty {
                HStack(spacing: 16) {
                    Label(profile.industry, systemImage: "building.2")
                        .font(.caption)
                        .foregroundStyle(.secondary)
                    Spacer()
                }
            }

            // Advanced price info
            if let quote = quote {
                StockAdvancedPriceView(quote: quote)
            }

            // Detailed financial metrics
            if let financials = financials {
                StockFinancialsDetailView(financials: financials)
            }

            // Company info
            if let details = details {
                StockCompanyInfoView(details: details)
            }

            // Delete StockProfile
            Button(role: .destructive) {
                showDeleteAllAlert = true
            } label: {
                Label(String(localized: .deleteAlertTitle), systemImage: "trash")
                    .font(.body)
                    .foregroundStyle(.red)
                    .frame(maxWidth: .infinity, alignment: .center)
                    .padding(.vertical, 8)
            }
            .alert(
                String(localized: .profileDeleteStockConfirmTitle(stockName: profile.name)),
                isPresented: $showDeleteAllAlert
            ) {
                Button(String(localized: .deleteAlertCancel), role: .cancel) {}
                Button(String(localized: .deleteAlertConfirm), role: .destructive) { deleteThisStock() }
            }
        }
    }

    @MainActor
    private func deleteThisStock() {
        do {
            try withAnimation {
                try stockEnvironment.dataManager.deleteAllData(for: profile.symbol)
            }
        } catch {
            print("Failed to delete data for symbol: \(profile.symbol): \(error)")
        }
    }
}

