import Charts
import SwiftUI
import StockModel

/// ì„±ëŠ¥ ìµœì í™”ëœ ì¼ì£¼ì¼ê°„ ê°€ê²© ë³€ë™ ì°¨íŠ¸ ë·°
struct OptimizedWeeklyPriceChartView: View {
    let data: [WeeklyChartData]
    let symbol: String
    let isLoading: Bool

    @State private var selectedPeriod: ChartPeriod = .oneWeek
    @State private var showFlexibleChart = false

    // ìµœì í™”: í•œ ë²ˆ ê³„ì‚°ëœ ê°’ë“¤ì„ ìºì‹œ
    @State private var cachedMetrics: ChartMetrics?
    @State private var previousDataCount: Int = 0

    @Environment(\.priceChartService) private var priceChartService

    /// ì°¨íŠ¸ ë©”íŠ¸ë¦­ì„ ìºì‹œí•˜ì—¬ ì„±ëŠ¥ ìµœì í™”
    fileprivate struct ChartMetrics {
        let priceChangePercentage: Double
        let isPositive: Bool
        let chartColor: Color
        let yAxisDomain: ClosedRange<Double>
        let chartHeight: CGFloat
        let dataCount: Int
    }

    /// ìºì‹œëœ ë©”íŠ¸ë¦­ ë˜ëŠ” ìƒˆë¡œ ê³„ì‚°ëœ ë©”íŠ¸ë¦­ ë°˜í™˜
    private var metrics: ChartMetrics {
        // ë°ì´í„°ê°€ ë³€ê²½ë˜ì§€ ì•Šì•˜ìœ¼ë©´ ìºì‹œëœ ê°’ ì‚¬ìš©
        if let cached = cachedMetrics, cached.dataCount == data.count, data.count == previousDataCount {
            return cached
        }

        // ìƒˆë¡œìš´ ë©”íŠ¸ë¦­ ê³„ì‚°
        let priceChange = WeeklyChartDataProcessor.calculatePriceChange(data)
        let isPositive = priceChange >= 0
        let color: Color = isPositive ? .green : .red
        let yDomain = calculateYAxisDomain()
        let height = calculateChartHeight(changePercent: abs(priceChange))

        let newMetrics = ChartMetrics(
            priceChangePercentage: priceChange,
            isPositive: isPositive,
            chartColor: color,
            yAxisDomain: yDomain,
            chartHeight: height,
            dataCount: data.count
        )

        // ìºì‹œ ì—…ë°ì´íŠ¸ (ë‹¤ìŒ í”„ë ˆì„ì—ì„œ)
        DispatchQueue.main.async {
            cachedMetrics = newMetrics
            previousDataCount = data.count
        }

        return newMetrics
    }

    /// Yì¶• ë„ë©”ì¸ ê³„ì‚° (ë©”ëª¨ì´ì œì´ì…˜)
    private func calculateYAxisDomain() -> ClosedRange<Double> {
        guard !data.isEmpty else { return 0...100 }

        let prices = data.map { $0.price }
        guard let minPrice = prices.min(), let maxPrice = prices.max() else {
            return 0...100
        }

        // ì„±ëŠ¥ ìµœì í™”: ë¯¸ë¦¬ ê³„ì‚°ëœ ê°’ ì‚¬ìš©
        let range = maxPrice - minPrice
        let padding = max(range * 0.1, 0.01)

        let adjustedMin = max(0, minPrice - padding)
        let adjustedMax = maxPrice + padding

        return adjustedMin...adjustedMax
    }

    /// ë™ì  ì°¨íŠ¸ ë†’ì´ ê³„ì‚°
    private func calculateChartHeight(changePercent: Double) -> CGFloat {
        guard !data.isEmpty else { return 60 }

        switch changePercent {
        case 5.0...:
            return 100
        case 2.0..<5.0:
            return 80
        default:
            return 60
        }
    }

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            // í—¤ë” (ìµœì í™”: ë¶ˆí•„ìš”í•œ ì—°ì‚° ì œê±°)
            HeaderView(
                selectedPeriod: selectedPeriod,
                priceChangePercentage: metrics.priceChangePercentage,
                chartColor: metrics.chartColor,
                onPeriodChange: { showFlexibleChart = true }
            )

            // ì°¨íŠ¸ (ìµœì í™”: ì¡°ê±´ë¶€ ë Œë”ë§ ê°œì„ )
            ChartContentView(
                data: data,
                isLoading: isLoading,
                metrics: metrics,
                onTap: { showFlexibleChart = true }
            )

            // ê¸°ê°„ ì •ë³´ (ìµœì í™”: ì¡°ê±´ë¶€ ë Œë”ë§)
            if !data.isEmpty {
                FooterView(data: data)
            }
        }
        .padding(.vertical, 4)
        .sheet(isPresented: $showFlexibleChart) {
            NavigationView {
                VStack {
                    // ìƒíƒœ ë™ê¸°í™”: selectedPeriod ì „ë‹¬
                    FlexiblePriceChartView(
                        symbol: symbol
                    )
                    .padding()

                    Spacer()
                }
                .navigationTitle("ìƒì„¸ ì°¨íŠ¸")
                .navigationBarTitleDisplayMode(.inline)
                .toolbar {
                    ToolbarItem(placement: .navigationBarTrailing) {
                        Button("ì™„ë£Œ") {
                            showFlexibleChart = false
                        }
                    }
                }
            }
            .presentationDetents([.medium, .large])
            .presentationDragIndicator(.visible)
        }
    }
}

// MARK: - ì„œë¸Œë·° ì»´í¬ë„ŒíŠ¸ë“¤ (ì„±ëŠ¥ ìµœì í™”ë¥¼ ìœ„í•œ ë¶„ë¦¬)

/// í—¤ë” ë·° (ì„±ëŠ¥ ìµœì í™”ë¥¼ ìœ„í•œ ë¶„ë¦¬)
private struct HeaderView: View {
    let selectedPeriod: ChartPeriod
    let priceChangePercentage: Double
    let chartColor: Color
    let onPeriodChange: () -> Void

    var body: some View {
        HStack {
            Label("\(selectedPeriod.displayName) ë³€ë™", systemImage: "chart.line.uptrend.xyaxis")
                .font(.caption.weight(.medium))
                .foregroundStyle(.secondary)

            Spacer()

            // ê¸°ê°„ ì „í™˜ ë²„íŠ¼
            Button(action: onPeriodChange) {
                HStack(spacing: 2) {
                    Image(systemName: "calendar")
                        .font(.caption2)
                    Text("ê¸°ê°„ ë³€ê²½")
                        .font(.caption2)
                }
                .foregroundStyle(.blue)
                .padding(.horizontal, 6)
                .padding(.vertical, 2)
                .background(
                    RoundedRectangle(cornerRadius: 4)
                        .fill(.blue.opacity(0.1))
                )
            }

            // ë³€ë™ë¥  í‘œì‹œ
            Text("\(priceChangePercentage >= 0 ? "+" : "")\(priceChangePercentage, specifier: "%.2f")%")
                .font(.caption.weight(.semibold))
                .foregroundStyle(chartColor)
        }
    }
}

/// ì°¨íŠ¸ ì½˜í…ì¸  ë·° (ì„±ëŠ¥ ìµœì í™”)
private struct ChartContentView: View {
    let data: [WeeklyChartData]
    let isLoading: Bool
    let metrics: OptimizedWeeklyPriceChartView.ChartMetrics
    let onTap: () -> Void

    var body: some View {
        Group {
            if isLoading {
                LoadingView()
            } else if data.isEmpty {
                OptimizedEmptyStateView()
            } else {
                // ìµœì í™”: ì°¨íŠ¸ ë Œë”ë§ ìµœì í™”
                OptimizedChart(data: data, metrics: metrics)
                    .onTapGesture(perform: onTap)
            }
        }
        .animation(.easeInOut(duration: 0.3), value: data.count)
    }
}

/// ìµœì í™”ëœ ì°¨íŠ¸ (ë Œë”ë§ ì„±ëŠ¥ ê°œì„ )
private struct OptimizedChart: View {
    let data: [WeeklyChartData]
    let metrics: OptimizedWeeklyPriceChartView.ChartMetrics

    var body: some View {
        Chart(data) { item in
            LineMark(
                x: .value("Date", item.date),
                y: .value("Price", item.price)
            )
            .foregroundStyle(metrics.chartColor)
            .lineStyle(StrokeStyle(lineWidth: 2.5))
            .symbol(.circle)
            .symbolSize(30)

            AreaMark(
                x: .value("Date", item.date),
                y: .value("Price", item.price)
            )
            .foregroundStyle(
                LinearGradient(
                    colors: [
                        metrics.chartColor.opacity(0.4),
                        metrics.chartColor.opacity(0.05)
                    ],
                    startPoint: .top,
                    endPoint: .bottom
                )
            )
        }
        .frame(height: metrics.chartHeight)
        .chartYScale(domain: metrics.yAxisDomain)
        .chartXAxis(.hidden)
        .chartYAxis(.hidden)
        .chartLegend(.hidden)
        .clipShape(RoundedRectangle(cornerRadius: 8))
    }
}

/// ë¡œë”© ìƒíƒœ ë·° (ì„±ëŠ¥ ìµœì í™”)
private struct LoadingView: View {
    var body: some View {
        RoundedRectangle(cornerRadius: 8)
            .fill(Color.gray.opacity(0.05))
            .frame(height: 60)
            .overlay {
                HStack(spacing: 8) {
                    ProgressView()
                        .scaleEffect(0.8)
                        .progressViewStyle(CircularProgressViewStyle(tint: .secondary))

                    Text("ì°¨íŠ¸ ë¡œë”© ì¤‘...")
                        .font(.caption2)
                        .foregroundStyle(.secondary)
                }
            }
            .overlay {
                RoundedRectangle(cornerRadius: 8)
                    .stroke(Color.gray.opacity(0.1), lineWidth: 1)
            }
    }
}

/// ë¹ˆ ìƒíƒœ ë·° (ì„±ëŠ¥ ìµœì í™”)
private struct OptimizedEmptyStateView: View {
    var body: some View {
        RoundedRectangle(cornerRadius: 8)
            .fill(Color.gray.opacity(0.05))
            .frame(height: 60)
            .overlay {
                VStack(spacing: 4) {
                    Image(systemName: "chart.line.downtrend.xyaxis")
                        .font(.caption)
                        .foregroundStyle(.tertiary)

                    Text("ì°¨íŠ¸ ë°ì´í„° ì—†ìŒ")
                        .font(.caption2)
                        .foregroundStyle(.tertiary)
                }
            }
            .overlay {
                RoundedRectangle(cornerRadius: 8)
                    .stroke(Color.gray.opacity(0.1), lineWidth: 1)
            }
    }
}

/// í‘¸í„° ë·° (ì„±ëŠ¥ ìµœì í™”)
private struct FooterView: View {
    let data: [WeeklyChartData]

    var body: some View {
        HStack {
            if let firstDate = data.first?.date,
               let lastDate = data.last?.date {
                Text("\(firstDate, format: .dateTime.month(.abbreviated).day()) - \(lastDate, format: .dateTime.month(.abbreviated).day())")
                    .font(.caption2)
                    .foregroundStyle(.tertiary)
            }

            Spacer()

            Text("\(data.count)ê°œ ë°ì´í„° í¬ì¸íŠ¸")
                .font(.caption2)
                .foregroundStyle(.tertiary)
        }
    }
}

// MARK: - í”„ë¦¬ë·°

#Preview("Optimized Upward Trend") {
    OptimizedWeeklyPriceChartView(
        data: [
            WeeklyChartData(timestamp: Int(Date().timeIntervalSince1970) - 6*24*3600, price: 150.0),
            WeeklyChartData(timestamp: Int(Date().timeIntervalSince1970) - 5*24*3600, price: 152.5),
            WeeklyChartData(timestamp: Int(Date().timeIntervalSince1970) - 4*24*3600, price: 149.8),
            WeeklyChartData(timestamp: Int(Date().timeIntervalSince1970) - 3*24*3600, price: 151.2),
            WeeklyChartData(timestamp: Int(Date().timeIntervalSince1970) - 2*24*3600, price: 153.1),
            WeeklyChartData(timestamp: Int(Date().timeIntervalSince1970) - 1*24*3600, price: 154.7),
            WeeklyChartData(timestamp: Int(Date().timeIntervalSince1970), price: 158.9)
        ],
        symbol: "AAPL",
        isLoading: false
    )
    .padding()
}

#Preview("Optimized Loading") {
    OptimizedWeeklyPriceChartView(data: [], symbol: "AAPL", isLoading: true)
        .padding()
}

