import Charts
import SwiftUI
import StockModel

/// í„°ì¹˜ë¡œ ê¸°ê°„ì„ ì „í™˜í•  ìˆ˜ ìˆëŠ” ì‹¬í”Œí•œ ê°€ê²© ì°¨íŠ¸ ë·°
struct PriceChartView: View {
    let symbol: String
    @State private var currentPeriod: ChartPeriod = .oneWeek
    @State private var chartData: [WeeklyChartData] = []
    @State private var isLoading = false
    @State private var hasError = false

    @Environment(\.priceChartService) private var priceChartService

    // ìŠ¤ì™€ì´í”„ ë˜ëŠ” íƒ­ìœ¼ë¡œ ë‹¤ìŒ ê¸°ê°„ìœ¼ë¡œ ì „í™˜
    private var nextPeriod: ChartPeriod {
        switch currentPeriod {
        case .oneDay: return .oneWeek
        case .oneWeek: return .oneMonth
        case .oneMonth: return .threeMonths
        case .threeMonths: return .sixMonths
        case .sixMonths: return .oneYear
        case .oneYear: return .oneDay
        }
    }

    // ìºì‹œëœ ë©”íŠ¸ë¦­ ê³„ì‚°
    private var priceChangePercentage: Double {
        WeeklyChartDataProcessor.calculatePriceChange(chartData)
    }

    private var chartColor: Color {
        priceChangePercentage >= 0 ? .green : .red
    }

    private var yAxisDomain: ClosedRange<Double> {
        guard !chartData.isEmpty else { return 0...100 }

        let prices = chartData.map { $0.price }
        guard let minPrice = prices.min(), let maxPrice = prices.max() else {
            return 0...100
        }

        let range = maxPrice - minPrice
        let padding = max(range * 0.1, 0.01)

        let adjustedMin = max(0, minPrice - padding)
        let adjustedMax = maxPrice + padding

        return adjustedMin...adjustedMax
    }

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            // ì‹¬í”Œí•œ í—¤ë”
            HStack {
                Text(currentPeriod.displayName)
                    .font(.caption.weight(.medium))
                    .foregroundStyle(.secondary)

                Spacer()

                if !chartData.isEmpty {
                    Text("\(priceChangePercentage >= 0 ? "+" : "")\(priceChangePercentage, specifier: "%.2f")%")
                        .font(.caption.weight(.semibold))
                        .foregroundStyle(chartColor)
                }
            }

            // ì°¨íŠ¸ ì»¨í…Œì´ë„ˆ
            ZStack {
                if isLoading {
                    loadingView
                } else if hasError || chartData.isEmpty {
                    emptyStateView
                } else {
                    chartView
                }
            }
            .frame(height: 80)
            .contentShape(Rectangle())
            .onTapGesture {
                withAnimation(.easeInOut(duration: 0.3)) {
                    switchToNextPeriod()
                }
            }
            .gesture(
                DragGesture(minimumDistance: 50)
                    .onEnded { value in
                        withAnimation(.easeInOut(duration: 0.3)) {
                            if value.translation.width < -30 {
                                // ì™¼ìª½ ìŠ¤ì™€ì´í”„ - ë‹¤ìŒ ê¸°ê°„ìœ¼ë¡œ
                                switchToNextPeriod()
                            } else if value.translation.width > 30 {
                                // ì˜¤ë¥¸ìª½ ìŠ¤ì™€ì´í”„ - ì´ì „ ê¸°ê°„ìœ¼ë¡œ
                                switchToPreviousPeriod()
                            }
                        }
                    }
            )
        }
        .padding(.vertical, 4)
        .onAppear {
            Task {
                await loadChartData()
            }
        }
    }

    // MARK: - Views

    private var chartView: some View {
        Chart(chartData) { item in
            LineMark(
                x: .value("Date", item.date),
                y: .value("Price", item.price)
            )
            .foregroundStyle(chartColor)
            .lineStyle(StrokeStyle(lineWidth: 2))

            AreaMark(
                x: .value("Date", item.date),
                y: .value("Price", item.price)
            )
            .foregroundStyle(
                LinearGradient(
                    colors: [
                        chartColor.opacity(0.3),
                        chartColor.opacity(0.05)
                    ],
                    startPoint: .top,
                    endPoint: .bottom
                )
            )
        }
        .chartYScale(domain: yAxisDomain)
        .chartXAxis(.hidden)
        .chartYAxis(.hidden)
        .chartLegend(.hidden)
        .transition(.opacity.combined(with: .scale(scale: 0.95)))
    }

    private var loadingView: some View {
        HStack {
            ProgressView()
                .scaleEffect(0.8)
                .progressViewStyle(CircularProgressViewStyle(tint: .secondary))

            Text("ë¡œë”© ì¤‘...")
                .font(.caption2)
                .foregroundStyle(.secondary)
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .background(Color.gray.opacity(0.05))
        .clipShape(RoundedRectangle(cornerRadius: 8))
    }

    private var emptyStateView: some View {
        VStack(spacing: 4) {
            Image(systemName: "chart.line.downtrend.xyaxis")
                .font(.title3)
                .foregroundStyle(.tertiary)

            Text("ë°ì´í„° ì—†ìŒ")
                .font(.caption)
                .foregroundStyle(.tertiary)

            Text("íƒ­í•˜ì—¬ ìƒˆë¡œê³ ì¹¨")
                .font(.caption2)
                .foregroundStyle(.blue)
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .background(Color.gray.opacity(0.05))
        .clipShape(RoundedRectangle(cornerRadius: 8))
    }

    // MARK: - Methods

    private func switchToNextPeriod() {
        currentPeriod = nextPeriod
        Task {
            await loadChartData()
        }
    }

    private func switchToPreviousPeriod() {
        // ì—­ìˆœìœ¼ë¡œ ì´ë™
        let allPeriods: [ChartPeriod] = [.oneDay, .oneWeek, .oneMonth, .threeMonths, .sixMonths, .oneYear]
        if let currentIndex = allPeriods.firstIndex(of: currentPeriod) {
            let previousIndex = currentIndex == 0 ? allPeriods.count - 1 : currentIndex - 1
            currentPeriod = allPeriods[previousIndex]
            Task {
                await loadChartData()
            }
        }
    }

    private func loadChartData() async {
        guard let service = priceChartService else {
            hasError = true
            return
        }

        // PriceChartServiceì˜ loadChartDataëŠ” voidì´ë¯€ë¡œ ë¨¼ì € ë¡œë“œë¥¼ ì‹œì‘í•˜ê³ 
        await service.loadChartData(for: symbol, period: currentPeriod)

        // ë¡œë”© ìƒíƒœ ê°ì‹œë¥¼ ìœ„í•œ íƒ€ì´ë¨¸
        var checkCount = 0
        while service.isLoading(for: symbol, period: currentPeriod) && checkCount < 30 {
            try? await Task.sleep(nanoseconds: 100_000_000) // 0.1ì´ˆ
            checkCount += 1
        }

        // ë°ì´í„° ê°€ì ¸ì˜¤ê¸° - PriceChartDataë¥¼ WeeklyChartDataë¡œ ë³€í™˜
        let priceData = service.getChartData(for: symbol, period: currentPeriod)
        chartData = priceData.map { data in
            WeeklyChartData(timestamp: Int(data.date.timeIntervalSince1970), price: data.price)
        }

        // ì—ëŸ¬ í™•ì¸
        if let error = service.getError(for: symbol, period: currentPeriod) {
            print("Chart loading error: \(error)")
            hasError = true
        }

        isLoading = service.isLoading(for: symbol, period: currentPeriod)
    }
}


// MARK: - Preview

#Preview("Price Chart") {
    PriceChartView(symbol: "AAPL")
        .padding()
}

#Preview("Multiple Charts") {
    VStack(spacing: 16) {
        PriceChartView(symbol: "AAPL")
        PriceChartView(symbol: "GOOGL")
        PriceChartView(symbol: "MSFT")
    }
    .padding()
}

