import SwiftUI
import SwiftData
import StockData
import StockAPI

@main
struct StocksApp: App {
    private let stockEnvironment: StockEnvironment
    private let priceChartService: PriceChartService
    private let weeklyChartService: WeeklyChartService

    init() {
        do {
            // StockEnvironment ìƒì„± (StockDataManager í¬í•¨)
            self.stockEnvironment = try StockEnvironment.production()

            // API í´ë¼ì´ì–¸íŠ¸ ìƒì„±
            let stockAPI = YahooFinanceClient()

            // ì°¨íŠ¸ ì„œë¹„ìŠ¤ë“¤ ì´ˆê¸°í™”
            self.priceChartService = PriceChartService(stockAPI: stockAPI)
            self.weeklyChartService = WeeklyChartService(stockAPI: stockAPI)
        } catch {
            fatalError("Could not create StockEnvironment: \(error)")
        }
    }

    var body: some Scene {
        WindowGroup {
            ContentView()
                .environment(stockEnvironment)
                .environment(\.priceChartService, priceChartService)
                .environment(\.weeklyChartService, weeklyChartService)
        }
        .modelContainer(stockEnvironment.container!)
    }
}

