//
//  ContentView.swift
//  Stocks
//
//  Created by oozoofrog on 6/10/25.
//

import StockData
import StockModel
import SwiftUI
import SwiftData
import StockAPI

struct ContentView: View {
    @Environment(StockEnvironment.self) private var stockEnvironment
    @State private var showSearchTicker: Bool = false

    @State private var tab: Tab = .stocks

    @State private var search: String = ""

    var body: some View {
        NavigationStack {
            if stockEnvironment.isChartMode {
                ChartView()
            } else {
                MainScrollView()
                    .navigationTitle(String(localized: "navigation.my_profiles"))
                    .searchable(text: $search, placement: .navigationBarDrawer(displayMode: .automatic))
                    .searchToolbarBehavior(.automatic)
                    .onChange(of: search) { oldValue, newValue in
                        guard oldValue != newValue else { return }
                        withAnimation {
                            stockEnvironment.search = newValue
                        }
                    }
            }
        }
        .overlay {
            VStack(alignment: .trailing) {
                Spacer()
                BottomMenus(viewModel: AddTickerViewModel(dataManager: stockEnvironment.dataManager))
            }
        }
    }

    enum Tab: Hashable, CaseIterable, Identifiable {

        var id: Int { hashValue }

        case stocks
        case charts

        var title: String {
            switch self {
            case .stocks:
                return "Stocks"
            case .charts:
                return "Charts"
            }
        }
    }
}

