import Foundation
import StockModel

// MARK: - Yahoo Finance Client
public actor YahooFinanceClient {
    private let authenticator: YahooFinanceAuthenticator
    private let isMockMode: Bool

    public init(authenticator: YahooFinanceAuthenticator = YahooFinanceAuthenticator()) {
        self.authenticator = authenticator
        self.isMockMode = false
    }

    /// Mock ë°ì´í„°ë¥¼ ì‚¬ìš©í•˜ëŠ” ì´ˆê¸°í™” (í”„ë¦¬ë·°/í…ŒìŠ¤íŠ¸ìš©)
    public init(useMockData: Bool) {
        self.authenticator = YahooFinanceAuthenticator()
        self.isMockMode = useMockData
    }

    public func request(_ url: String, params: [String: String] = [:]) async throws -> Data {
        do {
            try await authenticator.authenticate()
            return try await authenticator.makeAuthenticatedRequest(url: url, params: params)
        } catch YahooFinanceError.authenticationFailed {
            // ì¸ì¦ ì‹¤íŒ¨ ì‹œ í•œ ë²ˆ ë” ì‹œë„
            print("Authentication failed, retrying once...")
            try await authenticator.authenticate()
            return try await authenticator.makeAuthenticatedRequest(url: url, params: params)
        }
    }


    /// ê°€ê²© ë°ì´í„° API - /v8/finance/chart/{ticker}
    public func getHistory(symbol: String, period: String = "1d", interval: String = "1d", includePrePost: Bool = false, events: String = "div,splits,capitalGains") async throws -> ChartAPIResponse {
        let url = "https://query2.finance.yahoo.com/v8/finance/chart/\(symbol)"
        let params = [
            "period": period,
            "interval": interval,
            "includePrePost": includePrePost ? "true" : "false",
            "events": events
        ]

        let data = try await request(url, params: params)

        let decoder = JSONDecoder()
        do {
            return try decoder.decode(ChartAPIResponse.self, from: data)
        } catch {
            throw YahooFinanceError.decodingError(error)
        }
    }


    /// ì¢…ëª© ì •ë³´ API - /v10/finance/quoteSummary/{ticker}
