    /// Mock ì£¼ì‹ ì‹œì„¸ ë°ì´í„° ìƒì„±
    private func generateMockQuote(symbols: String) -> QuoteAPIResponse {
        let symbolList = symbols.split(separator: ",").map { String($0.trimmingCharacters(in: .whitespaces)) }

        let quotes = symbolList.map { symbol in
            let price = generateMockPrice(for: symbol)
            let change = Double.random(in: -5.0...10.0)

            return StockQuoteData(
                symbol: symbol,
                price: price,
                change: change,
                changePercent: (change / price) * 100,
                currency: "USD",
                marketState: "REGULAR"
            )
        }

        return QuoteAPIResponse(
            quoteResponse: QuoteResponseContainer(
                result: quotes,
                error: nil
            )
        )
    }

    /// Mock ì‹¬ë³¼ ê²€ìƒ‰ ë°ì´í„° ìƒì„±
    private func generateMockSearchResults(query: String) -> SearchAPIResponse {
        let mockResults = [
            SearchQuote(
                exchange: "NASDAQ",
                shortname: "\(query.uppercased()) Inc.",
                quoteType: "EQUITY",
