    /// Mock ì°¨íŠ¸ ë°ì´í„° ìƒì„±
    private func generateMockChartData(symbol: String, period: String, interval: String) -> ChartAPIResponse {
        let now = Date()
        let calendar = Calendar.current

        // ê¸°ê°„ì— ë”°ë¥¸ ë°ì´í„° í¬ì¸íŠ¸ ìˆ˜ ê²°ì •
        let dataPoints: Int
        let timeInterval: TimeInterval

        switch period {
        case "1d":
            dataPoints = 78 // 1ì¼ê°„ 5ë¶„ ê°„ê²©
            timeInterval = 5 * 60
        case "5d", "7d":
            dataPoints = 35 // 5ì¼ê°„ 1ì‹œê°„ ê°„ê²©  
            timeInterval = 60 * 60
        case "1mo":
            dataPoints = 22 // 1ë‹¬ê°„ 1ì¼ ê°„ê²©
            timeInterval = 24 * 60 * 60
        case "3mo":
            dataPoints = 65 // 3ë‹¬ê°„ 1ì¼ ê°„ê²©
            timeInterval = 24 * 60 * 60
        case "6mo":
            dataPoints = 26 // 6ë‹¬ê°„ 1ì£¼ ê°„ê²©
            timeInterval = 7 * 24 * 60 * 60
        case "1y":
            dataPoints = 52 // 1ë…„ê°„ 1ì£¼ ê°„ê²©
            timeInterval = 7 * 24 * 60 * 60
        default:
            dataPoints = 20
            timeInterval = 24 * 60 * 60
        }

        // ì‹¬ë³¼ë³„ ê¸°ë³¸ ê°€ê²© ì„¤ì •
        let basePrice: Double
        switch symbol.uppercased() {
        case "AAPL":
            basePrice = 180.0
        case "GOOGL", "GOOG":
            basePrice = 140.0
        case "MSFT":
            basePrice = 380.0
        case "TSLA":
            basePrice = 250.0
        case "NVDA":
            basePrice = 450.0
        case "AMZN":
            basePrice = 150.0
        default:
            basePrice = 100.0
        }

        // ì‹œê°„ ë°°ì—´ê³¼ ê°€ê²© ë°°ì—´ ìƒì„±
        var timestamps: [Int] = []
        var prices: [Double] = []

        let startTime = now.addingTimeInterval(-timeInterval * Double(dataPoints))

        for i in 0..<dataPoints {
            let timestamp = startTime.addingTimeInterval(timeInterval * Double(i))
            timestamps.append(Int(timestamp.timeIntervalSince1970))

            // ì‹¬í”Œí•œ ëœë¤ ì›Œí¬ë¡œ ê°€ê²© ìƒì„±
            let variation = Double.random(in: -0.05...0.05) // Â±5% ë³€ë™
            let price = basePrice * (1.0 + variation * Double(i) / Double(dataPoints))
            prices.append(price)
        }

        // Chart API Response ìƒì„±
        let quote = ChartQuote(close: prices)
        let indicators = ChartIndicators(quote: [quote])

        let result = ChartResult(
            meta: ChartMetaData(
                currency: "USD",
                symbol: symbol,
                exchangeName: "NASDAQ",
                regularMarketPrice: prices.last
            ),
            timestamp: timestamps,
            indicators: indicators
        )

        return ChartAPIResponse(
            chart: ChartResponseContainer(
                result: [result],
                error: nil
            )
        )
    }

