    /// Mock ì£¼ì‹ ì‹œì„¸ ë°ì´í„° ìƒì„±
    private func generateMockQuote(symbols: String) -> QuoteAPIResponse {
        let symbolList = symbols.split(separator: ",").map { String($0.trimmingCharacters(in: .whitespaces)) }

