        case "1d": (count, interval) = (78, 5 * 60)        // 5ë¶„ ê°„ê²©
        case "5d", "7d": (count, interval) = (35, 60 * 60) // 1ì‹œê°„ ê°„ê²©
        case "1mo": (count, interval) = (22, 24 * 60 * 60) // 1ì¼ ê°„ê²©
        case "3mo": (count, interval) = (65, 24 * 60 * 60) // 1ì¼ ê°„ê²©
        case "6mo": (count, interval) = (26, 7 * 24 * 60 * 60) // 1ì£¼ ê°„ê²©
        case "1y": (count, interval) = (52, 7 * 24 * 60 * 60)  // 1ì£¼ ê°„ê²©
        default: (count, interval) = (20, 24 * 60 * 60)
        }

        let startTime = now.addingTimeInterval(-interval * Double(count))
        return (0..<count).map { i in
            Int(startTime.addingTimeInterval(interval * Double(i)).timeIntervalSince1970)
        }
    }

    /// ê°€ê²© ë°°ì—´ ìƒì„± (ëœë¤ ì›Œí¬)
    private func generatePrices(basePrice: Double, count: Int) -> [Double] {
        var prices: [Double] = []
        var currentPrice = basePrice

        for _ in 0..<count {
            let variation = Double.random(in: -0.02...0.02) // Â±2% ë³€ë™
            currentPrice *= (1.0 + variation)
            prices.append(currentPrice)
        }

        return prices
    }
}

