    /// ê¸°ê°„ë³„ íƒ€ì„ìŠ¤íƒ¬í”„ ìƒì„±
    private func generateTimestamps(period: String) -> [Int] {
        let now = Date()
        let count: Int
        let interval: TimeInterval

        switch period {
        case "1d": (count, interval) = (78, 5 * 60)        // 5ë¶„ ê°„ê²©
        case "5d", "7d": (count, interval) = (35, 60 * 60) // 1ì‹œê°„ ê°„ê²©
