    private func generateMockPrice(for symbol: String) -> Double {
        switch symbol.uppercased() {
        case "AAPL": return Double.random(in: 170...190)
        case "GOOGL", "GOOG": return Double.random(in: 130...150)
        case "MSFT": return Double.random(in: 370...390)
        case "TSLA": return Double.random(in: 240...260)
