          }
        }
        """
        return json.data(using: String.Encoding.utf8)!
    }

    /// Mock Quote ë°ì´í„° JSON ìƒì„±
    private func generateMockQuoteJSON(symbols: String) -> Data {
        let symbolList = symbols.split(separator: ",").map { String($0.trimmingCharacters(in: .whitespaces)) }

        let quotes = symbolList.map { symbol in
            let price = generateMockPrice(for: symbol)
            let change = Double.random(in: -5.0...10.0)
            let changePercent = (change / price) * 100

            return """
            {
              "basicInfo": {
                "symbol": "\(symbol)",
                "longName": "\(symbol) Corporation",
                "shortName": "\(symbol)"
              },
              "price": {
                "regularMarketPrice": \(price),
                "regularMarketChange": \(change),
                "regularMarketChangePercent": \(changePercent),
                "regularMarketDayHigh": \(price * 1.05),
                "regularMarketDayLow": \(price * 0.95),
                "regularMarketOpen": \(price * 0.98),
                "regularMarketPreviousClose": \(price - change),
                "regularMarketVolume": \(Int.random(in: 1000000...10000000)),
                "regularMarketTime": \(Int(Date().timeIntervalSince1970))
              }
            }
            """
        }.joined(separator: ",")

        let json = """
        {
          "quoteResponse": {
            "result": [\(quotes)],
            "error": null
          }
        }
        """
        return json.data(using: String.Encoding.utf8)!
    }

    /// Mock Search ë°ì´í„° JSON ìƒì„±
    private func generateMockSearchJSON(query: String) -> Data {
        let json = """
        {
          "explains": [],
          "count": 1,
          "quotes": [
            {
              "exchange": "NASDAQ",
              "shortname": "\(query.uppercased()) Inc.",
              "quoteType": "EQUITY",
              "symbol": "\(query.uppercased())",
              "index": "quotes",
              "score": 1000000.0,
              "typeDisp": "Equity",
              "longname": "\(query.uppercased()) Corporation",
              "exchDisp": "NASDAQ",
              "sector": "Technology",
              "sectorDisp": "Technology",
              "industry": "Consumer Electronics",
              "industryDisp": "Consumer Electronics",
              "dispSecIndFlag": true,
              "isYahooFinance": true
            }
          ],
          "news": [],
          "nav": [],
          "lists": [],
          "researchReports": [],
          "screenerFieldResults": [],
          "totalTime": 12,
          "timeTakenForQuotes": 8,
          "timeTakenForNews": 2,
          "timeTakenForAlgowatchlist": 400,
          "timeTakenForPredefinedScreener": 400,
          "timeTakenForCrunchbase": 400,
          "timeTakenForNav": 400,
          "timeTakenForResearchReports": 0,
          "timeTakenForScreenerField": 400,
          "timeTakenForCulturalAssets": 400,
          "timeTakenForSearchLists": 400
        }
        """
        return json.data(using: String.Encoding.utf8)!
    }

    // MARK: - Helper Methods

    /// ì‹¬ë³¼ë³„ Mock ê°€ê²© ìƒì„±
    private func generateMockPrice(for symbol: String) -> Double {
        switch symbol.uppercased() {
        case "AAPL": return Double.random(in: 170...190)
        case "GOOGL", "GOOG": return Double.random(in: 130...150)
        case "MSFT": return Double.random(in: 370...390)
        case "TSLA": return Double.random(in: 240...260)
        case "NVDA": return Double.random(in: 440...460)
        case "AMZN": return Double.random(in: 140...160)
        default: return Double.random(in: 90...110)
        }
    }

    /// ê¸°ê°„ë³„ íƒ€ì„ìŠ¤íƒ¬í”„ ìƒì„±
    private func generateTimestamps(period: String) -> [Int] {
        let now = Date()
        let count: Int
        let interval: TimeInterval

        switch period {
        case "1d": (count, interval) = (78, 5 * 60)        // 5ë¶„ ê°„ê²©
        case "5d", "7d": (count, interval) = (35, 60 * 60) // 1ì‹œê°„ ê°„ê²©
        case "1mo": (count, interval) = (22, 24 * 60 * 60) // 1ì¼ ê°„ê²©
        case "3mo": (count, interval) = (65, 24 * 60 * 60) // 1ì¼ ê°„ê²©
        case "6mo": (count, interval) = (26, 7 * 24 * 60 * 60) // 1ì£¼ ê°„ê²©
        case "1y": (count, interval) = (52, 7 * 24 * 60 * 60)  // 1ì£¼ ê°„ê²©
        default: (count, interval) = (20, 24 * 60 * 60)
        }

        let startTime = now.addingTimeInterval(-interval * Double(count))
        return (0..<count).map { i in
            Int(startTime.addingTimeInterval(interval * Double(i)).timeIntervalSince1970)
        }
    }

    /// ê°€ê²© ë°°ì—´ ìƒì„± (ëœë¤ ì›Œí¬)
    private func generatePrices(basePrice: Double, count: Int) -> [Double] {
        var prices: [Double] = []
        var currentPrice = basePrice

        for _ in 0..<count {
            let variation = Double.random(in: -0.02...0.02) // Â±2% ë³€ë™
            currentPrice *= (1.0 + variation)
            prices.append(currentPrice)
        }

        return prices
    }
}

