            if let quoteData = response.quoteResponse.result.first {
                // API ì‘ë‹µì„ SwiftDataì— ì €ì¥í•˜ê³  ìºì‹œì— ì €ì¥
                do {
                    try dataManager.saveFromAPIResponse(quoteData: quoteData)

                    // ì €ì¥ëœ ë°ì´í„°ë¥¼ ìºì‹œì— ë¡œë“œ
                    if let savedQuote = dataManager.getQuote(for: symbol) {
                        priceCache[symbol] = savedQuote
                        lastUpdateTime[symbol] = Date()

                        // êµ¬ë…ìë“¤ì—ê²Œ ì•Œë¦¼
                        notifySubscribers(for: symbol, with: savedQuote)
                    }
                } catch {
                    print("Failed to save quote data: \(error)")
                }
            }

        } catch {
            print("Failed to load price for \(symbol): \(error)")
        }
    }

    /// ëª¨ë“  í™œì„± ì¢…ëª© ê°€ê²© ë¡œë“œ (ë°°ì¹˜ ì²˜ë¦¬)
    private func loadAllPrices() async {
        let symbols = Array(activeSymbols)
        guard !symbols.isEmpty else { return }

        // ë°°ì¹˜ í¬ê¸°ë¡œ ë‚˜ëˆ„ì–´ ì²˜ë¦¬
        for batch in symbols.chunked(into: Constants.maxBatchSize) {
            await loadBatch(symbols: batch)
        }
    }

    /// ë°°ì¹˜ ë‹¨ìœ„ë¡œ ê°€ê²© ë¡œë“œ
    private func loadBatch(symbols: [String]) async {
        let symbolString = symbols.joined(separator: ",")

        // ë¡œë”© ìƒíƒœ ì„¤ì •
        for symbol in symbols {
            loadingSymbols.insert(symbol)
        }

        defer {
            // ë¡œë”© ìƒíƒœ í•´ì œ
            for symbol in symbols {
                loadingSymbols.remove(symbol)
            }
        }

        do {
            let response = try await stockAPI.getQuote(symbols: symbolString)

            for quoteData in response.quoteResponse.result {
                let symbol = quoteData.basicInfo.symbol

                // API ì‘ë‹µì„ SwiftDataì— ì €ì¥í•˜ê³  ìºì‹œì— ì €ì¥
                do {
                    try dataManager.saveFromAPIResponse(quoteData: quoteData)

                    // ì €ì¥ëœ ë°ì´í„°ë¥¼ ìºì‹œì— ë¡œë“œ
                    if let savedQuote = dataManager.getQuote(for: symbol) {
                        priceCache[symbol] = savedQuote
                        lastUpdateTime[symbol] = Date()

                        // êµ¬ë…ìë“¤ì—ê²Œ ì•Œë¦¼
                        notifySubscribers(for: symbol, with: savedQuote)
                    }
                } catch {
                    print("Failed to save quote data: \(error)")
                }
            }

        } catch {
            print("Failed to load batch prices for \(symbolString): \(error)")
        }
    }

    /// êµ¬ë…ìë“¤ì—ê²Œ ì—…ë°ì´íŠ¸ ì•Œë¦¼
    private func notifySubscribers(for symbol: String, with data: StockQuoteData) {
        // ì•½í•œ ì°¸ì¡° ì •ë¦¬
        if let symbolSubscribers = subscribers[symbol] {
            subscribers[symbol] = Set(symbolSubscribers.filter { $0.object != nil })
        }

        // êµ¬ë…ìë“¤ì—ê²Œ ì•Œë¦¼ (ì‹¤ì œ êµ¬í˜„ì—ì„œëŠ” Combineì´ë‚˜ NotificationCenter ì‚¬ìš© ê³ ë ¤)
        // í˜„ì¬ëŠ” @Observableì„ í†µí•´ ìë™ìœ¼ë¡œ UI ì—…ë°ì´íŠ¸ë¨
    }

    // ì‹±ê¸€í†¤ì´ë¯€ë¡œ deinitì€ í˜¸ì¶œë˜ì§€ ì•ŠìŒ
    // í•„ìš”ì‹œ stopService()ë¡œ ì •ë¦¬
}

// MARK: - Helper Types

/// ì•½í•œ ì°¸ì¡° ë˜í¼
private class WeakRef: Hashable {
    weak var object: AnyObject?
    private let objectId: ObjectIdentifier

    init(_ object: AnyObject) {
        self.object = object
        self.objectId = ObjectIdentifier(object)
    }

    static func == (lhs: WeakRef, rhs: WeakRef) -> Bool {
        return lhs.objectId == rhs.objectId
    }

    func hash(into hasher: inout Hasher) {
        hasher.combine(objectId)
    }
}

// MARK: - Array Extension

private extension Array {
    func chunked(into size: Int) -> [[Element]] {
        return stride(from: 0, to: count, by: size).map {
            Array(self[$0..<Swift.min($0 + size, count)])
        }
    }
}

