import Foundation
import StockAPI
import StockModel
import SwiftUI

/// ë‹¤ì–‘í•œ ê¸°ê°„ì˜ ê°€ê²© ì°¨íŠ¸ ë°ì´í„°ë¥¼ ê´€ë¦¬í•˜ëŠ” ì„œë¹„ìŠ¤
@MainActor
@Observable
final class PriceChartService {

    /// ì°¨íŠ¸ ë°ì´í„° ìºì‹œ (ì‹¬ë³¼ë³„, ê¸°ê°„ë³„)
    private var chartDataCache: [String: [ChartPeriod: [PriceChartData]]] = [:]

    /// ë¡œë”© ìƒíƒœ (ì‹¬ë³¼ë³„, ê¸°ê°„ë³„)
    private var loadingStates: [String: [ChartPeriod: Bool]] = [:]

    /// ì—ëŸ¬ ìƒíƒœ (ì‹¬ë³¼ë³„, ê¸°ê°„ë³„)
    private var errorStates: [String: [ChartPeriod: Error?]] = [:]

    /// ìºì‹œ íƒ€ì„ìŠ¤íƒ¬í”„ (ì‹¬ë³¼ë³„, ê¸°ê°„ë³„)
    private var cacheTimestamps: [String: [ChartPeriod: Date]] = [:]

    /// Yahoo Finance API í´ë¼ì´ì–¸íŠ¸
    private let stockAPI: YahooFinanceClient

    /// ì´ˆê¸°í™”
    /// - Parameter stockAPI: Yahoo Finance API í´ë¼ì´ì–¸íŠ¸
    init(stockAPI: YahooFinanceClient) {
        self.stockAPI = stockAPI
    }

    /// íŠ¹ì • ì‹¬ë³¼ê³¼ ê¸°ê°„ì˜ ì°¨íŠ¸ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
    /// - Parameters:
    ///   - symbol: ì£¼ì‹ ì‹¬ë³¼
    ///   - period: ì°¨íŠ¸ ê¸°ê°„
    /// - Returns: ì°¨íŠ¸ ë°ì´í„° ë°°ì—´
    func getChartData(for symbol: String, period: ChartPeriod) -> [PriceChartData] {
        return chartDataCache[symbol]?[period] ?? []
    }

    /// íŠ¹ì • ì‹¬ë³¼ê³¼ ê¸°ê°„ì˜ ë¡œë”© ìƒíƒœ í™•ì¸
    /// - Parameters:
    ///   - symbol: ì£¼ì‹ ì‹¬ë³¼
    ///   - period: ì°¨íŠ¸ ê¸°ê°„
    /// - Returns: ë¡œë”© ì¤‘ì´ë©´ true
    func isLoading(for symbol: String, period: ChartPeriod) -> Bool {
        return loadingStates[symbol]?[period] ?? false
    }

    /// íŠ¹ì • ì‹¬ë³¼ê³¼ ê¸°ê°„ì˜ ì—ëŸ¬ ìƒíƒœ í™•ì¸
    /// - Parameters:
    ///   - symbol: ì£¼ì‹ ì‹¬ë³¼
    ///   - period: ì°¨íŠ¸ ê¸°ê°„
    /// - Returns: ì—ëŸ¬ê°€ ìˆìœ¼ë©´ í•´ë‹¹ ì—ëŸ¬, ì—†ìœ¼ë©´ nil
    func getError(for symbol: String, period: ChartPeriod) -> Error? {
        return errorStates[symbol]?[period] ?? nil
    }

    /// ìºì‹œëœ ë°ì´í„°ê°€ ìœ íš¨í•œì§€ í™•ì¸
    /// - Parameters:
    ///   - symbol: ì£¼ì‹ ì‹¬ë³¼
    ///   - period: ì°¨íŠ¸ ê¸°ê°„
    /// - Returns: ìºì‹œê°€ ìœ íš¨í•˜ë©´ true
    private func isCacheValid(for symbol: String, period: ChartPeriod) -> Bool {
        guard let timestamp = cacheTimestamps[symbol]?[period] else {
            return false
        }

        let validityDuration = ChartPeriodConfiguration.cacheValidityDuration(for: period)
        return Date().timeIntervalSince(timestamp) < validityDuration
    }

    /// ì°¨íŠ¸ ë°ì´í„° ë¡œë“œ
    /// - Parameters:
    ///   - symbol: ì£¼ì‹ ì‹¬ë³¼
    ///   - period: ì°¨íŠ¸ ê¸°ê°„
    ///   - forceRefresh: ìºì‹œ ë¬´ì‹œí•˜ê³  ê°•ì œ ìƒˆë¡œê³ ì¹¨
    func loadChartData(for symbol: String, period: ChartPeriod, forceRefresh: Bool = false) async {
        // ìœ íš¨í•œ ìºì‹œê°€ ìˆê³  ê°•ì œ ìƒˆë¡œê³ ì¹¨ì´ ì•„ë‹Œ ê²½ìš° ìŠ¤í‚µ
        if !forceRefresh && isCacheValid(for: symbol, period: period) {
            return
        }

        // ì´ë¯¸ ë¡œë”© ì¤‘ì¸ ê²½ìš° ì¤‘ë³µ ìš”ì²­ ë°©ì§€
        guard !isLoading(for: symbol, period: period) else { return }

        // ë¡œë”© ìƒíƒœ ì„¤ì •
        setLoadingState(for: symbol, period: period, isLoading: true)
        setError(for: symbol, period: period, error: nil)

        do {
            let response = try await stockAPI.getHistory(
                symbol: symbol,
                period: period.rawValue,
                interval: period.interval,
                includePrePost: false
            )

            // ì°¨íŠ¸ ë°ì´í„° ì²˜ë¦¬
            let chartData = PriceChartDataProcessor.processChartData(response, for: period)

            // ìºì‹œì— ì €ì¥
            setChartData(for: symbol, period: period, data: chartData)
            setCacheTimestamp(for: symbol, period: period, timestamp: Date())

        } catch {
