    /// ì—ëŸ¬ ìƒíƒœ ì„¤ì • (ë‚´ë¶€ ë©”ì„œë“œ)
    private func setError(for symbol: String, period: ChartPeriod, error: Error?) {
        if errorStates[symbol] == nil {
