import Charts
import SwiftUI
import StockModel

/// í„°ì¹˜ë¡œ ê¸°ê°„ì„ ì „í™˜í•  ìˆ˜ ìˆëŠ” ì‹¬í”Œí•œ ê°€ê²© ì°¨íŠ¸ ë·°
struct PriceChartView: View {
    let symbol: String
    @State private var currentPeriod: ChartPeriod = .oneWeek
    @State private var chartData: [WeeklyChartData] = []
    @State private var isLoading = false
    @State private var hasError = false

    @Environment(\.priceChartService) private var priceChartService

    // ìŠ¤ì™€ì´í”„ ë˜ëŠ” íƒ­ìœ¼ë¡œ ë‹¤ìŒ ê¸°ê°„ìœ¼ë¡œ ì „í™˜
    private var nextPeriod: ChartPeriod {
        switch currentPeriod {
        case .oneDay: return .oneWeek
        case .oneWeek: return .oneMonth
        case .oneMonth: return .threeMonths
        case .threeMonths: return .sixMonths
        case .sixMonths: return .oneYear
        case .oneYear: return .oneDay
        }
    }

    // ìºì‹œëœ ë©”íŠ¸ë¦­ ê³„ì‚°
    private var priceChangePercentage: Double {
        WeeklyChartDataProcessor.calculatePriceChange(chartData)
    }

    private var chartColor: Color {
        priceChangePercentage >= 0 ? .green : .red
    }

    private var yAxisDomain: ClosedRange<Double> {
        guard !chartData.isEmpty else { return 0...100 }

        let prices = chartData.map { $0.price }
        guard let minPrice = prices.min(), let maxPrice = prices.max() else {
            return 0...100
        }

        let range = maxPrice - minPrice
        let padding = max(range * 0.1, 0.01)

        let adjustedMin = max(0, minPrice - padding)
        let adjustedMax = maxPrice + padding

        return adjustedMin...adjustedMax

